import React from 'react';
import { StatusBar } from 'expo-status-bar';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { AuthProvider } from './src/context/AuthContext';
import { ThemeProvider } from './src/context/ThemeContext';

// Auth Screens
import LoginScreen from './src/screens/auth/LoginScreen';
import RegisterScreen from './src/screens/auth/RegisterScreen';

// Main Screens
import DashboardScreen from './src/screens/DashboardScreen';
import ProfileScreen from './src/screens/ProfileScreen';
import ApplicationsScreen from './src/screens/ApplicationsScreen';
import DocumentsScreen from './src/screens/DocumentsScreen';
import SupportScreen from './src/screens/SupportScreen';

// Utility Services
import UtilityServicesScreen from './src/screens/utility/UtilityServicesScreen';
import ServiceProvidersScreen from './src/screens/utility/ServiceProvidersScreen';
import DocumentUploadScreen from './src/screens/utility/DocumentUploadScreen';
import FinalFormScreen from './src/screens/utility/FinalFormScreen';

// Company Formation
import CompanyFormationScreen from './src/screens/company/CompanyFormationScreen';
import CompanyDocumentUploadScreen from './src/screens/company/CompanyDocumentUploadScreen';
import CompanyFormScreen from './src/screens/company/CompanyFormScreen';

// Government Grants
import GovernmentGrantsScreen from './src/screens/grants/GovernmentGrantsScreen';
import GrantCategoryScreen from './src/screens/grants/GrantCategoryScreen';
import GrantDetailScreen from './src/screens/grants/GrantDetailScreen';
import GrantApplicationScreen from './src/screens/grants/GrantApplicationScreen';

const Stack = createStackNavigator();

export default function App() {
  return (
    <ThemeProvider>
      <AuthProvider>
        <NavigationContainer>
          <Stack.Navigator
            initialRouteName="Login"
            screenOptions={{
              headerShown: false,
              cardStyle: { backgroundColor: '#F8FAFC' },
              cardStyleInterpolator: ({ current: { progress } }) => ({
                cardStyle: {
                  opacity: progress,
                },
              }),
            }}
          >
            {/* Auth Screens */}
            <Stack.Screen name="Login" component={LoginScreen} />
            <Stack.Screen name="Register" component={RegisterScreen} />

            {/* Main Screens */}
            <Stack.Screen name="Dashboard" component={DashboardScreen} />
            <Stack.Screen name="Profile" component={ProfileScreen} />
            <Stack.Screen name="Applications" component={ApplicationsScreen} />
            <Stack.Screen name="Documents" component={DocumentsScreen} />
            <Stack.Screen name="Support" component={SupportScreen} />

            {/* Utility Services */}
            <Stack.Screen name="UtilityServices" component={UtilityServicesScreen} />
            <Stack.Screen name="ServiceProviders" component={ServiceProvidersScreen} />
            <Stack.Screen name="DocumentUpload" component={DocumentUploadScreen} />
            <Stack.Screen name="FinalForm" component={FinalFormScreen} />

            {/* Company Formation */}
            <Stack.Screen name="CompanyFormation" component={CompanyFormationScreen} />
            <Stack.Screen name="CompanyDocumentUpload" component={CompanyDocumentUploadScreen} />
            <Stack.Screen name="CompanyForm" component={CompanyFormScreen} />

            {/* Government Grants */}
            <Stack.Screen name="GovernmentGrants" component={GovernmentGrantsScreen} />
            <Stack.Screen name="GrantCategory" component={GrantCategoryScreen} />
            <Stack.Screen name="GrantDetail" component={GrantDetailScreen} />
            <Stack.Screen name="GrantApplication" component={GrantApplicationScreen} />
          </Stack.Navigator>
          <StatusBar style="dark" />
        </NavigationContainer>
      </AuthProvider>
    </ThemeProvider>
  );
}
