import React, { useState } from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
  Alert,
} from 'react-native';
import { colors, spacing, typography, borderRadius, shadows } from '../../theme/colors';

const DocumentUploadScreen = ({ navigation, route }) => {
  const { service, provider } = route.params || {};
  const [uploadedDocs, setUploadedDocs] = useState([]);

  const requiredDocuments = [
    {
      id: 'identity',
      title: 'Identity Proof',
      subtitle: 'Aadhaar Card / PAN Card / Passport',
      required: true,
    },
    {
      id: 'address',
      title: 'Address Proof',
      subtitle: 'Utility Bill / Rent Agreement',
      required: true,
    },
    {
      id: 'photo',
      title: 'Passport Photo',
      subtitle: 'Recent photograph',
      required: true,
    },
    {
      id: 'property',
      title: 'Property Documents',
      subtitle: 'If applicable',
      required: false,
    },
  ];

  const handleUpload = (docId) => {
    Alert.alert(
      'Upload Document',
      'In production, this would open camera or file picker',
      [
        {
          text: 'Camera',
          onPress: () => {
            setUploadedDocs([...uploadedDocs, docId]);
            Alert.alert('Success', 'Document uploaded successfully!');
          },
        },
        {
          text: 'Gallery',
          onPress: () => {
            setUploadedDocs([...uploadedDocs, docId]);
            Alert.alert('Success', 'Document uploaded successfully!');
          },
        },
        { text: 'Cancel', style: 'cancel' },
      ]
    );
  };

  const handleContinue = () => {
    const requiredDocs = requiredDocuments.filter(doc => doc.required);
    const uploadedRequired = requiredDocs.filter(doc => 
      uploadedDocs.includes(doc.id)
    );

    if (uploadedRequired.length < requiredDocs.length) {
      Alert.alert('Error', 'Please upload all required documents');
      return;
    }

    navigation.navigate('FinalForm', { service, provider });
  };

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity
          onPress={() => navigation.goBack()}
          style={styles.backButton}
        >
          <Text style={styles.backIcon}>←</Text>
        </TouchableOpacity>
        <View style={styles.headerContent}>
          <Text style={styles.headerTitle}>Upload Documents</Text>
          <Text style={styles.headerSubtitle}>
            {service?.title || 'Service Application'}
          </Text>
        </View>
      </View>

      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Info Banner */}
        <View style={styles.infoBanner}>
          <Text style={styles.infoTitle}>Document Guidelines</Text>
          <Text style={styles.infoText}>
            • Upload clear, readable images{'\n'}
            • File size should be less than 5MB{'\n'}
            • Accepted formats: JPG, PNG, PDF
          </Text>
        </View>

        {/* Progress */}
        <View style={styles.progressContainer}>
          <View style={styles.progressStep}>
            <View style={[styles.progressDot, styles.progressDotActive]}>
              <Text style={styles.progressDotText}>1</Text>
            </View>
            <Text style={styles.progressLabel}>Service</Text>
          </View>
          <View style={styles.progressLine} />
          <View style={styles.progressStep}>
            <View style={[styles.progressDot, styles.progressDotActive]}>
              <Text style={styles.progressDotText}>2</Text>
            </View>
            <Text style={styles.progressLabel}>Provider</Text>
          </View>
          <View style={styles.progressLine} />
          <View style={styles.progressStep}>
            <View style={[styles.progressDot, styles.progressDotCurrent]}>
              <Text style={styles.progressDotText}>3</Text>
            </View>
            <Text style={styles.progressLabel}>Documents</Text>
          </View>
          <View style={styles.progressLine} />
          <View style={styles.progressStep}>
            <View style={styles.progressDot}>
              <Text style={styles.progressDotText}>4</Text>
            </View>
            <Text style={styles.progressLabel}>Submit</Text>
          </View>
        </View>

        {/* Documents List */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Required Documents</Text>
          {requiredDocuments.map((doc) => {
            const isUploaded = uploadedDocs.includes(doc.id);
            return (
              <View key={doc.id} style={styles.docCard}>
                <View style={styles.docHeader}>
                  <View style={styles.docInitial}>
                    <Text style={styles.docInitialText}>
                      {doc.title.charAt(0)}
                    </Text>
                  </View>
                  <View style={styles.docInfo}>
                    <View style={styles.docTitleRow}>
                      <Text style={styles.docTitle}>{doc.title}</Text>
                      {doc.required && (
                        <View style={styles.requiredBadge}>
                          <Text style={styles.requiredText}>Required</Text>
                        </View>
                      )}
                    </View>
                    <Text style={styles.docSubtitle}>{doc.subtitle}</Text>
                  </View>
                </View>
                
                {isUploaded ? (
                  <View style={styles.uploadedContainer}>
                    <View style={styles.uploadedBadge}>
                      <Text style={styles.uploadedText}>Uploaded</Text>
                    </View>
                    <TouchableOpacity
                      onPress={() => handleUpload(doc.id)}
                      style={styles.reuploadButton}
                    >
                      <Text style={styles.reuploadText}>Re-upload</Text>
                    </TouchableOpacity>
                  </View>
                ) : (
                  <TouchableOpacity
                    onPress={() => handleUpload(doc.id)}
                    style={styles.uploadButton}
                  >
                    <Text style={styles.uploadButtonText}>Upload</Text>
                  </TouchableOpacity>
                )}
              </View>
            );
          })}
        </View>

        {/* Continue Button */}
        <View style={styles.section}>
          <TouchableOpacity
            onPress={handleContinue}
            activeOpacity={0.7}
            style={styles.continueButton}
          >
            <Text style={styles.continueButtonText}>Continue to Form</Text>
          </TouchableOpacity>
        </View>

        <View style={{ height: spacing.xxl }} />
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.neutral.bg,
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: spacing.md,
    paddingVertical: spacing.md,
    backgroundColor: colors.neutral.surface,
    borderBottomWidth: 1,
    borderBottomColor: colors.neutral.border,
  },
  backButton: {
    marginRight: spacing.sm,
  },
  backIcon: {
    fontSize: typography.h2,
    color: colors.text.primary,
  },
  headerContent: {
    flex: 1,
  },
  headerTitle: {
    fontSize: typography.h3,
    fontWeight: typography.bold,
    color: colors.text.primary,
  },
  headerSubtitle: {
    fontSize: typography.tiny,
    color: colors.text.secondary,
    marginTop: spacing.xs,
  },
  infoBanner: {
    backgroundColor: colors.primary.bg,
    marginHorizontal: spacing.md,
    marginTop: spacing.lg,
    padding: spacing.md,
    borderRadius: borderRadius.md,
    borderLeftWidth: 4,
    borderLeftColor: colors.primary.main,
  },
  infoTitle: {
    fontSize: typography.body,
    fontWeight: typography.semibold,
    color: colors.primary.main,
    marginBottom: spacing.xs,
  },
  infoText: {
    fontSize: typography.small,
    color: colors.primary.main,
    lineHeight: 20,
  },
  progressContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    backgroundColor: colors.neutral.surface,
    borderRadius: borderRadius.md,
    padding: spacing.md,
    marginHorizontal: spacing.md,
    marginTop: spacing.lg,
    borderWidth: 1,
    borderColor: colors.neutral.border,
    ...shadows.sm,
  },
  progressStep: {
    alignItems: 'center',
  },
  progressDot: {
    width: 40,
    height: 40,
    borderRadius: 20,
    backgroundColor: colors.neutral.border,
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: spacing.sm,
  },
  progressDotActive: {
    backgroundColor: colors.status.success,
  },
  progressDotCurrent: {
    backgroundColor: colors.primary.main,
  },
  progressDotText: {
    fontSize: typography.small,
    fontWeight: typography.bold,
    color: colors.text.inverse,
  },
  progressLabel: {
    fontSize: typography.tiny,
    fontWeight: typography.semibold,
    color: colors.text.secondary,
  },
  progressLine: {
    flex: 1,
    height: 2,
    backgroundColor: colors.neutral.border,
    marginHorizontal: spacing.sm,
  },
  section: {
    paddingHorizontal: spacing.md,
    marginTop: spacing.lg,
  },
  sectionTitle: {
    fontSize: typography.h3,
    fontWeight: typography.bold,
    color: colors.text.primary,
    marginBottom: spacing.md,
  },
  docCard: {
    backgroundColor: colors.neutral.surface,
    borderRadius: borderRadius.md,
    padding: spacing.md,
    marginBottom: spacing.md,
    borderWidth: 1,
    borderColor: colors.neutral.border,
    ...shadows.sm,
  },
  docHeader: {
    flexDirection: 'row',
    marginBottom: spacing.md,
  },
  docInitial: {
    width: 48,
    height: 48,
    borderRadius: borderRadius.sm,
    backgroundColor: colors.primary.main,
    alignItems: 'center',
    justifyContent: 'center',
    marginRight: spacing.md,
  },
  docInitialText: {
    fontSize: typography.h3,
    fontWeight: typography.bold,
    color: colors.text.inverse,
  },
  docInfo: {
    flex: 1,
  },
  docTitleRow: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: spacing.xs,
  },
  docTitle: {
    fontSize: typography.body,
    fontWeight: typography.semibold,
    color: colors.text.primary,
    marginRight: spacing.sm,
  },
  requiredBadge: {
    backgroundColor: '#FEE2E2',
    paddingHorizontal: spacing.sm,
    paddingVertical: 2,
    borderRadius: borderRadius.sm,
  },
  requiredText: {
    fontSize: typography.tiny,
    fontWeight: typography.semibold,
    color: colors.status.error,
