import React from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';

export default function DashboardScreen({ navigation }) {
    const services = [
        { id: 1, title: 'Utility Services', icon: '‚ö°' },
        { id: 2, title: 'Grants & Improvements', icon: 'üí∞' },
        { id: 3, title: 'My Applications', icon: 'üìù' },
        { id: 4, title: 'Profile', icon: 'üë§' },
    ];

    return (
        <ScrollView style={styles.container}>
            <View style={styles.header}>
                <Text style={styles.greeting}>Welcome, User!</Text>
                <Text style={styles.subGreeting}>Access all government services in one place.</Text>
            </View>

            <View style={styles.grid}>
                {services.map((service) => (
                    <TouchableOpacity key={service.id} style={styles.card}>
                        <Text style={styles.icon}>{service.icon}</Text>
                        <Text style={styles.cardTitle}>{service.title}</Text>
                    </TouchableOpacity>
                ))}
            </View>
        </ScrollView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#f5f5f5',
    },
    header: {
        padding: 20,
        backgroundColor: '#fff',
        borderBottomWidth: 1,
        borderBottomColor: '#eee',
        marginBottom: 20,
    },
    greeting: {
        fontSize: 24,
        fontWeight: 'bold',
        color: '#333',
    },
    subGreeting: {
        fontSize: 14,
        color: '#666',
        marginTop: 5,
    },
    grid: {
        flexDirection: 'row',
        flexWrap: 'wrap',
        padding: 10,
        justifyContent: 'space-between',
    },
    card: {
        width: '48%',
        backgroundColor: '#fff',
        padding: 20,
        borderRadius: 12,
        marginBottom: 15,
        alignItems: 'center',
        shadowColor: '#000',
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.1,
        shadowRadius: 3.84,
        elevation: 5,
    },
    icon: {
        fontSize: 32,
        marginBottom: 10,
    },
    cardTitle: {
        fontSize: 14,
        fontWeight: '600',
        textAlign: 'center',
        color: '#333',
    },
});
