import React, { useState } from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { useTheme } from '../../context/ThemeContext';

const CompanyFormationScreen = ({ navigation }) => {
  const theme = useTheme();

  const services = [
    {
      id: 'gst',
      title: 'GST Registration',
      subtitle: '‡§ú‡•Ä‡§è‡§∏‡§ü‡•Ä ‡§™‡§Ç‡§ú‡•Ä‡§ï‡§∞‡§£',
      description: 'Register for Goods and Services Tax',
      icon: 'üìã',
      gradient: ['#3B82F6', '#2563EB'],
      duration: '7-10 days',
      fee: '‚Çπ2,999',
    },
    {
      id: 'pan',
      title: 'PAN Card',
      subtitle: '‡§™‡•à‡§® ‡§ï‡§æ‡§∞‡•ç‡§°',
      description: 'Permanent Account Number',
      icon: 'üÜî',
      gradient: ['#10B981', '#059669'],
      duration: '5-7 days',
      fee: '‚Çπ499',
    },
    {
      id: 'tan',
      title: 'TAN Registration',
      subtitle: '‡§ü‡•à‡§® ‡§™‡§Ç‡§ú‡•Ä‡§ï‡§∞‡§£',
      description: 'Tax Deduction Account Number',
      icon: 'üí≥',
      gradient: ['#F59E0B', '#D97706'],
      duration: '7-10 days',
      fee: '‚Çπ1,499',
    },
    {
      id: 'company',
      title: 'Company Registration',
      subtitle: '‡§ï‡§Ç‡§™‡§®‡•Ä ‡§™‡§Ç‡§ú‡•Ä‡§ï‡§∞‡§£',
      description: 'Private Limited, LLP, OPC',
      icon: 'üè¢',
      gradient: ['#8B5CF6', '#7C3AED'],
      duration: '15-20 days',
      fee: '‚Çπ9,999',
    },
  ];

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Header */}
        <LinearGradient
          colors={[theme.colors.primary[600], theme.colors.primary[700]]}
          style={styles.header}
        >
          <TouchableOpacity
            onPress={() => navigation.goBack()}
            style={styles.backButton}
          >
            <Text style={styles.backIcon}>‚Üê</Text>
          </TouchableOpacity>
          <View style={styles.headerContent}>
            <Text style={styles.headerTitle}>Business Registration</Text>
            <Text style={styles.headerSubtitle}>‡§µ‡•ç‡§Ø‡§µ‡§∏‡§æ‡§Ø ‡§™‡§Ç‡§ú‡•Ä‡§ï‡§∞‡§£</Text>
            <Text style={styles.headerDescription}>
              Register your business with government authorities
            </Text>
          </View>
        </LinearGradient>

        {/* Info Banner */}
        <View style={styles.section}>
          <View style={styles.infoBanner}>
            <Text style={styles.infoIcon}>üí°</Text>
            <View style={styles.infoContent}>
              <Text style={styles.infoTitle}>Quick & Easy Process</Text>
              <Text style={styles.infoText}>
                Complete registration online with minimal documentation
              </Text>
            </View>
          </View>
        </View>

        {/* Services */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Available Services</Text>
          {services.map((service) => (
            <TouchableOpacity
              key={service.id}
              onPress={() => {
                // Navigate to service details or form
                console.log('Selected:', service.title);
              }}
              activeOpacity={0.8}
            >
              <LinearGradient
                colors={service.gradient}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 1 }}
                style={styles.serviceCard}
              >
                <View style={styles.serviceHeader}>
                  <View style={styles.serviceIconContainer}>
                    <Text style={styles.serviceIcon}>{service.icon}</Text>
                  </View>
                  <View style={styles.serviceInfo}>
                    <Text style={styles.serviceTitle}>{service.title}</Text>
                    <Text style={styles.serviceSubtitle}>{service.subtitle}</Text>
                    <Text style={styles.serviceDescription}>
                      {service.description}
                    </Text>
                  </View>
                </View>
                <View style={styles.serviceFooter}>
                  <View style={styles.serviceDetail}>
                    <Text style={styles.detailIcon}>‚è±Ô∏è</Text>
                    <Text style={styles.detailText}>{service.duration}</Text>
                  </View>
                  <View style={styles.serviceDetail}>
                    <Text style={styles.detailIcon}>üí∞</Text>
                    <Text style={styles.detailText}>{service.fee}</Text>
                  </View>
                  <View style={styles.applyButton}>
                    <Text style={styles.applyButtonText}>Apply ‚Üí</Text>
                  </View>
                </View>
              </LinearGradient>
            </TouchableOpacity>
          ))}
        </View>

        {/* Features */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Why Choose Us?</Text>
          <View style={styles.featuresGrid}>
            {[
              { icon: '‚úÖ', title: 'Fast Processing', desc: 'Quick turnaround time' },
              { icon: 'üîí', title: 'Secure', desc: 'Data protection' },
              { icon: 'üí∞', title: 'Affordable', desc: 'Best prices' },
              { icon: 'üìû', title: 'Support', desc: '24/7 assistance' },
            ].map((feature, index) => (
              <View key={index} style={styles.featureCard}>
                <Text style={styles.featureIcon}>{feature.icon}</Text>
                <Text style={styles.featureTitle}>{feature.title}</Text>
                <Text style={styles.featureDesc}>{feature.desc}</Text>
              </View>
            ))}
          </View>
        </View>

        <View style={{ height: 40 }} />
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F8FAFC',
  },
  header: {
    paddingTop: 20,
    paddingBottom: 30,
    paddingHorizontal: 20,
    borderBottomLeftRadius: 30,
    borderBottomRightRadius: 30,
  },
  backButton: {
    width: 40,
    height: 40,
    borderRadius: 12,
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: 16,
  },
  backIcon: {
    fontSize: 24,
    color: '#FFFFFF',
    fontWeight: '700',
  },
  headerContent: {
    alignItems: 'center',
  },
  headerTitle: {
    fontSize: 28,
    fontWeight: '800',
    color: '#FFFFFF',
    marginBottom: 4,
  },
  headerSubtitle: {
    fontSize: 16,
    color: 'rgba(255, 255, 255, 0.9)',
    marginBottom: 8,
  },
  headerDescription: {
    fontSize: 14,
    color: 'rgba(255, 255, 255, 0.8)',
    textAlign: 'center',
  },
  section: {
    paddingHorizontal: 20,
    marginTop: 24,
  },
  infoBanner: {
    flexDirection: 'row',
    backgroundColor: '#DBEAFE',
    borderRadius: 16,
    padding: 16,
  },
  infoIcon: {
    fontSize: 24,
    marginRight: 12,
  },
  infoContent: {
    flex: 1,
  },
  infoTitle: {
    fontSize: 15,
    fontWeight: '700',
    color: '#1E40AF',
    marginBottom: 4,
  },
  infoText: {
    fontSize: 13,
    color: '#1E40AF',
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: '700',
    color: '#1E293B',
    marginBottom: 16,
  },
  serviceCard: {
    borderRadius: 20,
    padding: 20,
    marginBottom: 16,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 4 },
    shadowOpacity: 0.15,
    shadowRadius: 8,
    elevation: 4,
  },
  serviceHeader: {
    flexDirection: 'row',
    marginBottom: 16,
  },
  serviceIconContainer: {
    width: 56,
    height: 56,
    borderRadius: 16,
    backgroundColor: 'rgba(255, 255, 255, 0.3)',
    alignItems: 'center',
    justifyContent: 'center',
    marginRight: 12,
  },
  serviceIcon: {
    fontSize: 28,
  },
  serviceInfo: {
    flex: 1,
  },
  serviceTitle: {
    fontSize: 18,
    fontWeight: '700',
    color: '#FFFFFF',
    marginBottom: 2,
  },
  serviceSubtitle: {
    fontSize: 12,
    color: 'rgba(255, 255, 255, 0.8)',
    marginBottom: 4,
  },
  serviceDescription: {
    fontSize: 13,
    color: 'rgba(255, 255, 255, 0.9)',
  },
  serviceFooter: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  serviceDetail: {
    flexDirection: 'row',
    alignItems: 'center',
    marginRight: 16,
  },
  detailIcon: {
    fontSize: 14,
    marginRight: 4,
  },
  detailText: {
    fontSize: 13,
    fontWeight: '600',
    color: '#FFFFFF',
  },
  applyButton: {
    flex: 1,
    backgroundColor: 'rgba(255, 255, 255, 0.3)',
    borderRadius: 10,
    paddingVertical: 8,
    alignItems: 'center',
  },
  applyButtonText: {
    fontSize: 14,
    fontWeight: '700',
    color: '#FFFFFF',
  },
  featuresGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    marginHorizontal: -6,
  },
  featureCard: {
    width: '47%',
    backgroundColor: '#FFFFFF',
    borderRadius: 16,
    padding: 16,
    margin: 6,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.05,
    shadowRadius: 4,
    elevation: 2,
  },
  featureIcon: {
    fontSize: 32,
    marginBottom: 8,
  },
  featureTitle: {
    fontSize: 14,
    fontWeight: '700',
    color: '#1E293B',
    marginBottom: 4,
  },
  featureDesc: {
    fontSize: 12,
    color: '#64748B',
    textAlign: 'center',
  },
});

export default CompanyFormationScreen;
