import React, { useState } from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
  Alert,
} from 'react-native';
import { colors, spacing, typography, borderRadius, shadows } from '../../theme/colors';

const DocumentUploadScreen = ({ navigation, route }) => {
  const { service, provider } = route.params || {};
  const [uploadedDocs, setUploadedDocs] = useState({});
  const [uploading, setUploading] = useState(false);

  const requiredDocuments = [
    {
      id: 'identity',
      title: 'Identity Proof',
      subtitle: 'Aadhaar Card / PAN Card / Passport',
      required: true,
      formats: 'PDF, JPG, PNG',
      maxSize: '5 MB',
    },
    {
      id: 'address',
      title: 'Address Proof',
      subtitle: 'Utility Bill / Rent Agreement / Ration Card',
      required: true,
      formats: 'PDF, JPG, PNG',
      maxSize: '5 MB',
    },
    {
      id: 'photo',
      title: 'Passport Photo',
      subtitle: 'Recent photograph (white background)',
      required: true,
      formats: 'JPG, PNG',
      maxSize: '2 MB',
    },
    {
      id: 'property',
      title: 'Property Documents',
      subtitle: 'Property papers (if applicable)',
      required: false,
      formats: 'PDF, JPG, PNG',
      maxSize: '10 MB',
    },
  ];

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return Math.round(bytes / Math.pow(k, i) * 100) / 100 + ' ' + sizes[i];
  };

  const handleUploadMethod = (docId, docTitle, method) => {
    setUploading(true);

    // Simulate upload with timeout
    setTimeout(() => {
      const fileExtension = method === 'document' ? 'pdf' : 'jpg';
      const fileName = `${docTitle.replace(/\s+/g, '_')}_${Date.now()}.${fileExtension}`;
      const fileSize = Math.floor(Math.random() * 3000000) + 500000;

      const newDoc = {
        name: fileName,
        size: fileSize,
        uploadedAt: new Date().toISOString(),
        method: method,
        type: fileExtension,
      };

      setUploadedDocs(prev => ({
        ...prev,
        [docId]: newDoc,
      }));

      setUploading(false);

      Alert.alert(
        '‚úì Upload Successful!',
        `${docTitle} uploaded successfully!\n\nFile: ${fileName}\nSize: ${formatFileSize(fileSize)}\nMethod: ${method}`,
        [{ text: 'OK' }]
      );
    }, 800);
  };

  const handleUploadPress = (docId, docTitle) => {
    Alert.alert(
      `Upload ${docTitle}`,
      'Choose how you want to upload this document:',
      [
        {
          text: 'üì∑ Take Photo',
          onPress: () => handleUploadMethod(docId, docTitle, 'camera'),
        },
        {
          text: 'üñºÔ∏è Choose from Gallery',
          onPress: () => handleUploadMethod(docId, docTitle, 'gallery'),
        },
        {
          text: 'üìÑ Select PDF Document',
          onPress: () => handleUploadMethod(docId, docTitle, 'document'),
        },
        {
          text: 'Cancel',
          style: 'cancel',
        },
      ],
      { cancelable: true }
    );
  };

  const handleRemove = (docId, docTitle) => {
    Alert.alert(
      'Remove Document',
      `Are you sure you want to remove ${docTitle}?`,
      [
        {
          text: 'Remove',
          style: 'destructive',
          onPress: () => {
            const newDocs = { ...uploadedDocs };
            delete newDocs[docId];
            setUploadedDocs(newDocs);
            Alert.alert('‚úì Removed', `${docTitle} has been removed.`);
          },
        },
        {
          text: 'Cancel',
          style: 'cancel',
        },
      ]
    );
  };

  const handleContinue = () => {
    const requiredDocs = requiredDocuments.filter(doc => doc.required);
    const uploadedRequired = requiredDocs.filter(doc => uploadedDocs[doc.id]);

    if (uploadedRequired.length < requiredDocs.length) {
      const missing = requiredDocs.filter(doc => !uploadedDocs[doc.id]);
      const missingNames = missing.map(doc => doc.title).join('\n‚Ä¢ ');
      
      Alert.alert(
        '‚ö†Ô∏è Missing Required Documents',
        `Please upload the following documents:\n\n‚Ä¢ ${missingNames}\n\nProgress: ${uploadedRequired.length}/${requiredDocs.length}`,
        [{ text: 'OK' }]
      );
      return;
    }

    navigation.navigate('FinalForm', { 
      service, 
      provider, 
      documents: uploadedDocs 
    });
  };

  const getUploadProgress = () => {
    const required = requiredDocuments.filter(doc => doc.required).length;
    const uploaded = requiredDocuments.filter(doc => doc.required && uploadedDocs[doc.id]).length;
    return { uploaded, required };
  };

  const progress = getUploadProgress();
  const progressPercentage = progress.required > 0 ? (progress.uploaded / progress.required) * 100 : 0;

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity
          onPress={() => navigation.goBack()}
          style={styles.backButton}
          activeOpacity={0.7}
        >
          <Text style={styles.backIcon}>‚Üê</Text>
        </TouchableOpacity>
        <View style={styles.headerContent}>
          <Text style={styles.headerTitle}>Upload Documents</Text>
          <Text style={styles.headerSubtitle}>
            {provider?.name || 'DGVCL'} ‚Ä¢ {service?.title || 'Electricity Connection'}
          </Text>
        </View>
      </View>

      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Progress Card */}
        <View style={styles.progressCard}>
          <View style={styles.progressHeader}>
            <Text style={styles.progressTitle}>Upload Progress</Text>
            <Text style={styles.progressCount}>
              {progress.uploaded} of {progress.required} required
            </Text>
          </View>
          <View style={styles.progressBarContainer}>
            <View 
              style={[
                styles.progressBar, 
                { width: `${progressPercentage}%` }
              ]} 
            />
          </View>
          <Text style={styles.progressText}>
            {progress.uploaded === progress.required 
              ? '‚úì All required documents uploaded!' 
              : `${progress.required - progress.uploaded} more required`}
          </Text>
        </View>

        {/* Info Banner */}
        <View style={styles.infoBanner}>
          <View style={styles.infoBadge}>
            <Text style={styles.infoBadgeText}>TIPS</Text>
          </View>
          <View style={styles.infoContent}>
            <Text style={styles.infoText}>
              ‚Ä¢ Ensure documents are clear and readable{'\n'}
              ‚Ä¢ File size should be less than specified limit{'\n'}
              ‚Ä¢ Accepted formats: PDF, JPG, PNG
            </Text>
          </View>
        </View>

        {/* Documents List */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Required Documents</Text>
          {requiredDocuments.map((doc) => {
            const isUploaded = uploadedDocs[doc.id];
            return (
              <View key={doc.id} style={styles.docCard}>
                {/* Document Header */}
                <View style={styles.docHeader}>
                  <View style={[
                    styles.docInitial,
                    isUploaded && styles.docInitialUploaded
                  ]}>
                    <Text style={styles.docInitialText}>
                      {isUploaded ? '‚úì' : doc.title.charAt(0)}
                    </Text>
                  </View>
                  <View style={styles.docInfo}>
                    <View style={styles.docTitleRow}>
                      <Text style={styles.docTitle}>{doc.title}</Text>
                      {doc.required && !isUploaded && (
                        <View style={styles.requiredBadge}>
                          <Text style={styles.requiredText}>Required</Text>
                        </View>
                      )}
                      {isUploaded && (
                        <View style={styles.uploadedBadge}>
                          <Text style={styles.uploadedText}>‚úì Uploaded</Text>
                        </View>
                      )}
                    </View>
                    <Text style={styles.docSubtitle}>{doc.subtitle}</Text>
                    <View style={styles.docMeta}>
                      <Text style={styles.docMetaText}>
                        {doc.formats} ‚Ä¢ Max {doc.maxSize}
                      </Text>
                    </View>
                  </View>
                </View>

                {/* Uploaded Document Info */}
                {isUploaded && (
                  <View style={styles.uploadedContainer}>
                    <View style={styles.uploadedInfo}>
                      <Text style={styles.uploadedFileName} numberOfLines={1}>
                        üìÑ {isUploaded.name}
                      </Text>
                      <Text style={styles.uploadedFileSize}>
                        Size: {formatFileSize(isUploaded.size)} ‚Ä¢ Method: {isUploaded.method}
                      </Text>
                      <Text style={styles.uploadedFileDate}>
                        Uploaded: {new Date(isUploaded.uploadedAt).toLocaleString()}
                      </Text>
                    </View>
                    <View style={styles.uploadedActions}>
                      <TouchableOpacity
                        onPress={() => handleUploadPress(doc.id, doc.title)}
                        style={styles.actionButton}
                        activeOpacity={0.7}
                      >
                        <Text style={styles.actionButtonText}>Replace</Text>
                      </TouchableOpacity>
                      <TouchableOpacity
                        onPress={() => handleRemove(doc.id, doc.title)}
                        style={[styles.actionButton, styles.actionButtonDanger]}
                        activeOpacity={0.7}
                      >
                        <Text style={[styles.actionButtonText, styles.actionButtonTextDanger]}>
                          Remove
                        </Text>
                      </TouchableOpacity>
                    </View>
                  </View>
                )}

                {/* Upload Button */}
                {!isUploaded && (
                  <TouchableOpacity
                    onPress={() => {
                      console.log('Button clicked for:', doc.id, doc.title);
                      handleUploadPress(doc.id, doc.title);
                    }}
                    style={styles.uploadButton}
                    activeOpacity={0.7}
                    disabled={uploading}
                  >
                    <Text style={styles.uploadButtonText}>
                      {uploading ? 'Uploading...' : 'Upload Document'}
                    </Text>
                    <Text style={styles.uploadButtonIcon}>+</Text>
                  </TouchableOpacity>
                )}
              </View>
            );
          })}
        </View>

        {/* Continue Button */}
        <View style={styles.section}>
          <TouchableOpacity
            onPress={handleContinue}
            activeOpacity={0.7}
            style={[
              styles.continueButton,
              progress.uploaded < progress.required && styles.continueButtonDisabled
            ]}
            disabled={progress.uploaded < progress.required}
          >
            <Text style={styles.continueButtonText}>
              {progress.uploaded < progress.required 
                ? `Upload ${progress.required - progress.uploaded} More Required Documents` 
                : 'Continue to Application Form'}
            </Text>
            {progress.uploaded === progress.required && (
              <Text style={styles.continueButtonArrow}>‚Üí</Text>
            )}
          </TouchableOpacity>
          
          {progress.uploaded < progress.required && (
            <Text style={styles.continueHint}>
              üëÜ Tap "Upload Document" buttons above to upload required documents
            </Text>
          )}
        </View>

        <View style={{ height: spacing.xxl }} />
      </ScrollView>
    </SafeAreaView>
  );
};

