import React from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { useTheme } from '../../context/ThemeContext';

const UtilityServicesScreen = ({ navigation }) => {
  const theme = useTheme();

  const services = [
    {
      id: 'electricity',
      name: 'Electricity',
      nameHindi: '‡§¨‡§ø‡§ú‡§≤‡•Ä',
      icon: '‚ö°',
      gradient: ['#F59E0B', '#D97706'],
      description: 'Name change, new connection, bill payment',
    },
    {
      id: 'gas',
      name: 'Gas',
      nameHindi: '‡§ó‡•à‡§∏',
      icon: 'üî•',
      gradient: ['#EF4444', '#DC2626'],
      description: 'Name change, new connection, cylinder booking',
    },
    {
      id: 'water',
      name: 'Water',
      nameHindi: '‡§™‡§æ‡§®‡•Ä',
      icon: 'üíß',
      gradient: ['#3B82F6', '#2563EB'],
      description: 'Name change, new connection, bill payment',
    },
    {
      id: 'property',
      name: 'Property',
      nameHindi: '‡§∏‡§Ç‡§™‡§§‡•ç‡§§‡§ø',
      icon: 'üè†',
      gradient: ['#10B981', '#059669'],
      description: 'Name transfer, mutation, tax payment',
    },
  ];

  const handleServiceSelect = (service) => {
    navigation.navigate('ServiceProviders', { serviceType: service.id });
  };

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity
          onPress={() => navigation.goBack()}
          style={styles.backButton}
        >
          <Text style={styles.backIcon}>‚Üê</Text>
        </TouchableOpacity>
        <View style={styles.headerContent}>
          <Text style={styles.headerTitle}>Utility Services</Text>
          <Text style={styles.headerSubtitle}>‡§â‡§™‡§Ø‡•ã‡§ó‡§ø‡§§‡§æ ‡§∏‡•á‡§µ‡§æ‡§è‡§Ç</Text>
        </View>
      </View>

      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Banner */}
        <LinearGradient
          colors={[theme.colors.primary[600], theme.colors.primary[700]]}
          style={styles.banner}
        >
          <Text style={styles.bannerIcon}>‚ö°</Text>
          <Text style={styles.bannerTitle}>All Utility Services</Text>
          <Text style={styles.bannerDescription}>
            Manage all your utility services in one place
          </Text>
        </LinearGradient>

        {/* Services */}
        <View style={styles.servicesContainer}>
          {services.map((service) => (
            <TouchableOpacity
              key={service.id}
              onPress={() => handleServiceSelect(service)}
              activeOpacity={0.8}
            >
              <LinearGradient
                colors={service.gradient}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 1 }}
                style={styles.serviceCard}
              >
                <View style={styles.serviceIconContainer}>
                  <Text style={styles.serviceIcon}>{service.icon}</Text>
                </View>
                <View style={styles.serviceContent}>
                  <Text style={styles.serviceName}>{service.name}</Text>
                  <Text style={styles.serviceNameHindi}>{service.nameHindi}</Text>
                  <Text style={styles.serviceDescription}>{service.description}</Text>
                </View>
                <View style={styles.serviceArrow}>
                  <Text style={styles.arrowIcon}>‚Üí</Text>
                </View>
              </LinearGradient>
            </TouchableOpacity>
          ))}
        </View>

        {/* Features */}
        <View style={styles.featuresContainer}>
          <Text style={styles.featuresTitle}>Why Choose Us?</Text>
          {[
            { icon: 'üöÄ', title: 'Fast Processing', desc: 'Quick application processing' },
            { icon: 'üîí', title: 'Secure', desc: 'Your data is encrypted and safe' },
            { icon: 'üì±', title: 'Easy to Use', desc: 'Simple and intuitive interface' },
          ].map((feature, index) => (
            <View key={index} style={styles.featureCard}>
              <Text style={styles.featureIcon}>{feature.icon}</Text>
              <View style={styles.featureContent}>
                <Text style={styles.featureTitle}>{feature.title}</Text>
                <Text style={styles.featureDesc}>{feature.desc}</Text>
              </View>
            </View>
          ))}
        </View>

        <View style={{ height: 40 }} />
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F8FAFC',
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: 20,
    paddingVertical: 16,
    backgroundColor: '#FFFFFF',
    borderBottomWidth: 1,
    borderBottomColor: '#E2E8F0',
  },
  backButton: {
    marginRight: 12,
  },
  backIcon: {
    fontSize: 24,
    color: '#1E293B',
  },
  headerContent: {
    flex: 1,
  },
  headerTitle: {
    fontSize: 20,
    fontWeight: '700',
    color: '#1E293B',
  },
  headerSubtitle: {
    fontSize: 12,
    color: '#64748B',
    marginTop: 2,
  },
  banner: {
    margin: 20,
    padding: 24,
    borderRadius: 20,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 4 },
    shadowOpacity: 0.15,
    shadowRadius: 8,
    elevation: 4,
  },
  bannerIcon: {
    fontSize: 48,
    marginBottom: 12,
  },
  bannerTitle: {
    fontSize: 22,
    fontWeight: '700',
    color: '#FFFFFF',
    marginBottom: 8,
  },
  bannerDescription: {
    fontSize: 14,
    color: 'rgba(255, 255, 255, 0.9)',
    textAlign: 'center',
  },
  servicesContainer: {
    paddingHorizontal: 20,
  },
  serviceCard: {
    flexDirection: 'row',
    alignItems: 'center',
    borderRadius: 20,
    padding: 16,
    marginBottom: 12,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 4 },
    shadowOpacity: 0.15,
    shadowRadius: 8,
    elevation: 4,
  },
  serviceIconContainer: {
    width: 60,
    height: 60,
    borderRadius: 16,
    backgroundColor: 'rgba(255, 255, 255, 0.3)',
    alignItems: 'center',
    justifyContent: 'center',
    marginRight: 12,
  },
  serviceIcon: {
    fontSize: 32,
  },
  serviceContent: {
    flex: 1,
  },
  serviceName: {
    fontSize: 18,
    fontWeight: '700',
    color: '#FFFFFF',
    marginBottom: 2,
  },
  serviceNameHindi: {
    fontSize: 12,
    color: 'rgba(255, 255, 255, 0.8)',
    marginBottom: 4,
  },
  serviceDescription: {
    fontSize: 13,
    color: 'rgba(255, 255, 255, 0.9)',
  },
  serviceArrow: {
    width: 36,
    height: 36,
    borderRadius: 10,
    backgroundColor: 'rgba(255, 255, 255, 0.3)',
    alignItems: 'center',
    justifyContent: 'center',
  },
  arrowIcon: {
    fontSize: 20,
    color: '#FFFFFF',
    fontWeight: '700',
  },
  featuresContainer: {
    paddingHorizontal: 20,
    marginTop: 20,
  },
  featuresTitle: {
    fontSize: 18,
    fontWeight: '700',
    color: '#1E293B',
    marginBottom: 16,
  },
  featureCard: {
    flexDirection: 'row',
    backgroundColor: '#FFFFFF',
    borderRadius: 16,
    padding: 16,
    marginBottom: 12,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.05,
    shadowRadius: 4,
    elevation: 2,
  },
  featureIcon: {
    fontSize: 32,
    marginRight: 12,
  },
  featureContent: {
    flex: 1,
  },
  featureTitle: {
    fontSize: 16,
    fontWeight: '600',
    color: '#1E293B',
    marginBottom: 4,
  },
  featureDesc: {
    fontSize: 13,
    color: '#64748B',
  },
});

export default UtilityServicesScreen;
