import React from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { useTheme } from '../../context/ThemeContext';

const ServiceProvidersScreen = ({ navigation, route }) => {
  const theme = useTheme();
  const { serviceType } = route.params;

  const providers = {
    electricity: [
      { id: 'dgvcl', name: 'DGVCL', fullName: 'Dakshin Gujarat Vij Company Limited', coverage: 'South Gujarat', icon: '‚ö°' },
      { id: 'torrent', name: 'Torrent Power', fullName: 'Torrent Power Limited', coverage: 'Ahmedabad, Gandhinagar', icon: '‚ö°' },
      { id: 'ugvcl', name: 'UGVCL', fullName: 'Uttar Gujarat Vij Company Limited', coverage: 'North Gujarat', icon: '‚ö°' },
    ],
    gas: [
      { id: 'ggl', name: 'Gujarat Gas', fullName: 'Gujarat Gas Limited', coverage: 'All Gujarat', icon: 'üî•' },
      { id: 'adani', name: 'Adani Gas', fullName: 'Adani Total Gas Limited', coverage: 'Major Cities', icon: 'üî•' },
    ],
    water: [
      { id: 'amc', name: 'AMC Water', fullName: 'Ahmedabad Municipal Corporation', coverage: 'Ahmedabad', icon: 'üíß' },
      { id: 'smc', name: 'SMC Water', fullName: 'Surat Municipal Corporation', coverage: 'Surat', icon: 'üíß' },
    ],
    property: [
      { id: 'amc-prop', name: 'AMC Property', fullName: 'Ahmedabad Municipal Corporation', coverage: 'Ahmedabad', icon: 'üè†' },
      { id: 'smc-prop', name: 'SMC Property', fullName: 'Surat Municipal Corporation', coverage: 'Surat', icon: 'üè†' },
    ],
  };

  const currentProviders = providers[serviceType] || [];

  const handleProviderSelect = (provider) => {
    navigation.navigate('DocumentUpload', {
      serviceType,
      providerId: provider.id,
      providerName: provider.name,
    });
  };

  const getServiceTitle = () => {
    const titles = {
      electricity: 'Electricity Providers',
      gas: 'Gas Providers',
      water: 'Water Providers',
      property: 'Property Services',
    };
    return titles[serviceType] || 'Service Providers';
  };

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity
          onPress={() => navigation.goBack()}
          style={styles.backButton}
        >
          <Text style={styles.backIcon}>‚Üê</Text>
        </TouchableOpacity>
        <View style={styles.headerContent}>
          <Text style={styles.headerTitle}>{getServiceTitle()}</Text>
          <Text style={styles.headerSubtitle}>Select your service provider</Text>
        </View>
      </View>

      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Info Banner */}
        <View style={styles.infoBanner}>
          <Text style={styles.infoIcon}>‚ÑπÔ∏è</Text>
          <Text style={styles.infoText}>
            Select your service provider to continue with the application
          </Text>
        </View>

        {/* Providers List */}
        <View style={styles.providersContainer}>
          {currentProviders.map((provider) => (
            <TouchableOpacity
              key={provider.id}
              onPress={() => handleProviderSelect(provider)}
              activeOpacity={0.8}
            >
              <View style={styles.providerCard}>
                <View style={styles.providerIconContainer}>
                  <Text style={styles.providerIcon}>{provider.icon}</Text>
                </View>
                <View style={styles.providerContent}>
                  <Text style={styles.providerName}>{provider.name}</Text>
                  <Text style={styles.providerFullName}>{provider.fullName}</Text>
                  <View style={styles.coverageContainer}>
                    <Text style={styles.coverageIcon}>üìç</Text>
                    <Text style={styles.coverageText}>{provider.coverage}</Text>
                  </View>
                </View>
                <View style={styles.providerArrow}>
                  <Text style={styles.arrowIcon}>‚Üí</Text>
                </View>
              </View>
            </TouchableOpacity>
          ))}
        </View>

        {/* Help Section */}
        <View style={styles.helpSection}>
          <Text style={styles.helpTitle}>Can't find your provider?</Text>
          <Text style={styles.helpText}>
            Contact our support team for assistance
          </Text>
          <TouchableOpacity
            onPress={() => navigation.navigate('Support')}
            style={styles.helpButton}
          >
            <Text style={styles.helpButtonText}>Contact Support</Text>
          </TouchableOpacity>
        </View>

        <View style={{ height: 40 }} />
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F8FAFC',
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: 20,
    paddingVertical: 16,
    backgroundColor: '#FFFFFF',
    borderBottomWidth: 1,
    borderBottomColor: '#E2E8F0',
  },
  backButton: {
    marginRight: 12,
  },
  backIcon: {
    fontSize: 24,
    color: '#1E293B',
  },
  headerContent: {
    flex: 1,
  },
  headerTitle: {
    fontSize: 20,
    fontWeight: '700',
    color: '#1E293B',
  },
  headerSubtitle: {
    fontSize: 12,
    color: '#64748B',
    marginTop: 2,
  },
  infoBanner: {
    flexDirection: 'row',
    backgroundColor: '#DBEAFE',
    margin: 20,
    padding: 16,
    borderRadius: 16,
    alignItems: 'center',
  },
  infoIcon: {
    fontSize: 24,
    marginRight: 12,
  },
  infoText: {
    flex: 1,
    fontSize: 14,
    color: '#1E40AF',
    lineHeight: 20,
  },
  providersContainer: {
    paddingHorizontal: 20,
  },
  providerCard: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#FFFFFF',
    borderRadius: 20,
    padding: 16,
    marginBottom: 12,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.05,
    shadowRadius: 4,
    elevation: 2,
  },
  providerIconContainer: {
    width: 56,
    height: 56,
    borderRadius: 16,
    backgroundColor: '#F8FAFC',
    alignItems: 'center',
    justifyContent: 'center',
    marginRight: 12,
  },
  providerIcon: {
    fontSize: 28,
  },
  providerContent: {
    flex: 1,
  },
  providerName: {
    fontSize: 18,
    fontWeight: '700',
    color: '#1E293B',
    marginBottom: 4,
  },
  providerFullName: {
    fontSize: 13,
    color: '#64748B',
    marginBottom: 6,
  },
  coverageContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  coverageIcon: {
    fontSize: 12,
    marginRight: 4,
  },
  coverageText: {
    fontSize: 12,
    color: '#64748B',
  },
  providerArrow: {
    width: 36,
    height: 36,
    borderRadius: 10,
    backgroundColor: '#F8FAFC',
    alignItems: 'center',
    justifyContent: 'center',
  },
  arrowIcon: {
    fontSize: 20,
    color: '#2563EB',
    fontWeight: '700',
  },
  helpSection: {
    margin: 20,
    padding: 20,
    backgroundColor: '#FFFFFF',
    borderRadius: 20,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.05,
    shadowRadius: 4,
    elevation: 2,
  },
  helpTitle: {
    fontSize: 18,
    fontWeight: '700',
    color: '#1E293B',
    marginBottom: 8,
  },
  helpText: {
    fontSize: 14,
    color: '#64748B',
    textAlign: 'center',
    marginBottom: 16,
  },
  helpButton: {
    backgroundColor: '#2563EB',
    paddingHorizontal: 24,
    paddingVertical: 12,
    borderRadius: 12,
  },
  helpButtonText: {
    fontSize: 15,
    fontWeight: '700',
    color: '#FFFFFF',
  },
});

export default ServiceProvidersScreen;
