import React, { useState } from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
  Alert,
} from 'react-native';

const DocumentUploadScreen = ({ navigation, route }) => {
  const { service, provider } = route.params || {};
  const [uploadedDocs, setUploadedDocs] = useState({});

  const requiredDocuments = [
    {
      id: 'identity',
      title: 'Identity Proof',
      subtitle: 'Aadhaar Card / PAN Card / Passport',
      required: true,
    },
    {
      id: 'address',
      title: 'Address Proof',
      subtitle: 'Utility Bill / Rent Agreement',
      required: true,
    },
    {
      id: 'photo',
      title: 'Passport Photo',
      subtitle: 'Recent photograph',
      required: true,
    },
  ];

  const handleUpload = (docId, docTitle) => {
    Alert.alert(
      `Upload ${docTitle}`,
      'Choose upload method:',
      [
        {
          text: 'Camera',
          onPress: () => {
            const fileName = `${docId}_${Date.now()}.jpg`;
            const fileSize = Math.floor(Math.random() * 3000000) + 500000;
            
            setUploadedDocs(prev => ({
              ...prev,
              [docId]: {
                name: fileName,
                size: fileSize,
                method: 'camera',
                date: new Date().toLocaleString(),
              }
            }));

            Alert.alert('Success!', `Document uploaded: ${fileName}`);
          }
        },
        {
          text: 'Gallery',
          onPress: () => {
            const fileName = `${docId}_${Date.now()}.jpg`;
            const fileSize = Math.floor(Math.random() * 3000000) + 500000;
            
            setUploadedDocs(prev => ({
              ...prev,
              [docId]: {
                name: fileName,
                size: fileSize,
                method: 'gallery',
                date: new Date().toLocaleString(),
              }
            }));

            Alert.alert('Success!', `Document uploaded: ${fileName}`);
          }
        },
        {
          text: 'PDF',
          onPress: () => {
            const fileName = `${docId}_${Date.now()}.pdf`;
            const fileSize = Math.floor(Math.random() * 3000000) + 500000;
            
            setUploadedDocs(prev => ({
              ...prev,
              [docId]: {
                name: fileName,
                size: fileSize,
                method: 'pdf',
                date: new Date().toLocaleString(),
              }
            }));

            Alert.alert('Success!', `Document uploaded: ${fileName}`);
          }
        },
        { text: 'Cancel', style: 'cancel' },
      ]
    );
  };

  const handleRemove = (docId) => {
    Alert.alert(
      'Remove Document',
      'Are you sure?',
      [
        {
          text: 'Remove',
          style: 'destructive',
          onPress: () => {
            const newDocs = { ...uploadedDocs };
            delete newDocs[docId];
            setUploadedDocs(newDocs);
            Alert.alert('Removed', 'Document removed');
          }
        },
        { text: 'Cancel', style: 'cancel' },
      ]
    );
  };

  const formatSize = (bytes) => {
    return (bytes / 1024 / 1024).toFixed(2) + ' MB';
  };

  const progress = {
    uploaded: requiredDocuments.filter(doc => uploadedDocs[doc.id]).length,
    total: requiredDocuments.length,
  };

  const canContinue = progress.uploaded === progress.total;

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity 
          onPress={() => navigation.goBack()} 
          style={styles.backButton}
        >
          <Text style={styles.backText}>‚Üê Back</Text>
        </TouchableOpacity>
        <View style={styles.headerContent}>
          <Text style={styles.headerTitle}>Upload Documents</Text>
          <Text style={styles.headerSubtitle}>
            {provider?.name || 'Provider'} ‚Ä¢ {service?.title || 'Service'}
          </Text>
        </View>
      </View>

      <ScrollView style={styles.scrollView}>
        {/* Progress */}
        <View style={styles.progressCard}>
          <Text style={styles.progressTitle}>
            Progress: {progress.uploaded}/{progress.total}
          </Text>
          <View style={styles.progressBarBg}>
            <View 
              style={[
                styles.progressBarFill, 
                { width: `${(progress.uploaded / progress.total) * 100}%` }
              ]} 
            />
          </View>
          <Text style={styles.progressText}>
            {canContinue ? '‚úì All documents uploaded!' : `${progress.total - progress.uploaded} more needed`}
          </Text>
        </View>

        {/* Documents */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Required Documents</Text>
          
          {requiredDocuments.map((doc) => {
            const uploaded = uploadedDocs[doc.id];
            
            return (
              <View key={doc.id} style={styles.docCard}>
                <View style={styles.docHeader}>
                  <View style={[styles.badge, uploaded && styles.badgeSuccess]}>
                    <Text style={styles.badgeText}>
                      {uploaded ? '‚úì' : doc.title.charAt(0)}
                    </Text>
                  </View>
                  <View style={styles.docInfo}>
                    <Text style={styles.docTitle}>{doc.title}</Text>
                    <Text style={styles.docSubtitle}>{doc.subtitle}</Text>
                  </View>
                </View>

                {uploaded ? (
                  <View style={styles.uploadedBox}>
                    <Text style={styles.uploadedText}>
                      üìÑ {uploaded.name}
                    </Text>
                    <Text style={styles.uploadedMeta}>
                      {formatSize(uploaded.size)} ‚Ä¢ {uploaded.method} ‚Ä¢ {uploaded.date}
                    </Text>
                    <View style={styles.buttonRow}>
                      <TouchableOpacity
                        onPress={() => handleUpload(doc.id, doc.title)}
                        style={styles.smallBtn}
                      >
                        <Text style={styles.smallBtnText}>Replace</Text>
                      </TouchableOpacity>
                      <TouchableOpacity
                        onPress={() => handleRemove(doc.id)}
                        style={[styles.smallBtn, styles.smallBtnDanger]}
                      >
                        <Text style={[styles.smallBtnText, styles.smallBtnTextDanger]}>
                          Remove
                        </Text>
                      </TouchableOpacity>
                    </View>
                  </View>
                ) : (
                  <TouchableOpacity
                    onPress={() => handleUpload(doc.id, doc.title)}
                    style={styles.uploadBtn}
                  >
                    <Text style={styles.uploadBtnText}>Upload Document +</Text>
                  </TouchableOpacity>
                )}
              </View>
            );
          })}
        </View>

        {/* Continue */}
        <View style={styles.section}>
          <TouchableOpacity
            onPress={() => {
              if (canContinue) {
                navigation.navigate('FinalForm', { service, provider, documents: uploadedDocs });
              } else {
                Alert.alert('Missing Documents', `Please upload all ${progress.total} required documents`);
              }
            }}
            style={[styles.continueBtn, !canContinue && styles.continueBtnDisabled]}
            disabled={!canContinue}
          >
            <Text style={styles.continueBtnText}>
              {canContinue ? 'Continue to Form ‚Üí' : `Upload ${progress.total - progress.uploaded} More`}
            </Text>
          </TouchableOpacity>
        </View>

        <View style={{ height: 40 }} />
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F8FAFC',
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 16,
    backgroundColor: '#FFFFFF',
    borderBottomWidth: 1,
    borderBottomColor: '#E2E8F0',
  },
  backButton: {
    marginRight: 12,
    padding: 8,
  },
  backText: {
    fontSize: 16,
    color: '#1E40AF',
    fontWeight: '600',
  },
  headerContent: {
    flex: 1,
  },
  headerTitle: {
    fontSize: 20,
    fontWeight: '700',
    color: '#0F172A',
  },
  headerSubtitle: {
    fontSize: 12,
    color: '#475569',
    marginTop: 2,
  },
  scrollView: {
    flex: 1,
  },
  progressCard: {
    margin: 16,
    padding: 16,
    backgroundColor: '#FFFFFF',
    borderRadius: 12,
    borderWidth: 1,
    borderColor: '#E2E8F0',
  },
  progressTitle: {
    fontSize: 16,
    fontWeight: '600',
    color: '#0F172A',
    marginBottom: 8,
  },
  progressBarBg: {
    height: 8,
    backgroundColor: '#E2E8F0',
    borderRadius: 4,
    overflow: 'hidden',
    marginBottom: 8,
  },
  progressBarFill: {
    height: '100%',
    backgroundColor: '#1E40AF',
  },
  progressText: {
    fontSize: 14,
    color: '#475569',
  },
  section: {
    paddingHorizontal: 16,
    marginTop: 16,
  },
  sectionTitle: {
    fontSize: 18,
    fontWeight: '700',
    color: '#0F172A',
    marginBottom: 12,
  },
  docCard: {
    backgroundColor: '#FFFFFF',
    borderRadius: 12,
    padding: 16,
    marginBottom: 12,
    borderWidth: 1,
    borderColor: '#E2E8F0',
  },
  docHeader: {
    flexDirection: 'row',
    marginBottom: 12,
  },
  badge: {
    width: 48,
    height: 48,
    borderRadius: 8,
    backgroundColor: '#1E40AF',
    alignItems: 'center',
    justifyContent: 'center',
    marginRight: 12,
  },
  badgeSuccess: {
    backgroundColor: '#10B981',
  },
  badgeText: {
    fontSize: 20,
    fontWeight: '700',
    color: '#FFFFFF',
  },
  docInfo: {
    flex: 1,
    justifyContent: 'center',
  },
  docTitle: {
    fontSize: 16,
    fontWeight: '600',
    color: '#0F172A',
    marginBottom: 4,
  },
  docSubtitle: {
    fontSize: 14,
    color: '#475569',
  },
  uploadBtn: {
    backgroundColor: '#1E40AF',
    borderRadius: 8,
    padding: 14,
    alignItems: 'center',
    cursor: 'pointer',
  },
  uploadBtnText: {
    fontSize: 16,
    fontWeight: '600',
    color: '#FFFFFF',
  },
  uploadedBox: {
    backgroundColor: '#F8FAFC',
    borderRadius: 8,
    padding: 12,
  },
  uploadedText: {
    fontSize: 14,
    fontWeight: '600',
    color: '#0F172A',
    marginBottom: 4,
  },
  uploadedMeta: {
    fontSize: 12,
    color: '#475569',
    marginBottom: 12,
  },
  buttonRow: {
    flexDirection: 'row',
    gap: 8,
  },
  smallBtn: {
    flex: 1,
    padding: 10,
    borderRadius: 6,
    borderWidth: 1,
    borderColor: '#1E40AF',
    alignItems: 'center',
    cursor: 'pointer',
  },
  smallBtnDanger: {
    borderColor: '#EF4444',
  },
  smallBtnText: {
    fontSize: 14,
    fontWeight: '600',
    color: '#1E40AF',
