import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView, Platform, Dimensions } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { Ionicons } from '@expo/vector-icons';
import { SafeAreaView } from 'react-native-safe-area-context';
import { StatusBar } from 'expo-status-bar';

const { width } = Dimensions.get('window');

const ServiceHeader = ({ title, icon, color, navigation }) => (
    <View style={styles.header}>
        <LinearGradient
            colors={[color, color + '99']} // Add transparency
            start={{ x: 0, y: 0 }}
            end={{ x: 1, y: 1 }}
            style={styles.headerBackground}
        />
        <SafeAreaView style={styles.headerContent}>
            <TouchableOpacity onPress={() => navigation.goBack()} style={styles.backButton}>
                <Ionicons name="arrow-back" size={24} color="#FFF" />
            </TouchableOpacity>
            <View style={styles.titleContainer}>
                <View style={[styles.iconContainer, { backgroundColor: 'rgba(255,255,255,0.2)' }]}>
                    <Ionicons name={icon} size={28} color="#FFF" />
                </View>
                <Text style={styles.headerTitle}>{title}</Text>
            </View>
        </SafeAreaView>
    </View>
);

const ActionButton = ({ title, icon, onPress, color }) => (
    <TouchableOpacity style={styles.actionButton} onPress={onPress}>
        <View style={[styles.actionIcon, { backgroundColor: color + '15' }]}>
            <Ionicons name={icon} size={24} color={color} />
        </View>
        <Text style={styles.actionText}>{title}</Text>
        <Ionicons name="chevron-forward" size={20} color="#CCC" />
    </TouchableOpacity>
);

export default function ElectricityScreen({ navigation }) {
    const [balance, setBalance] = useState('₹ 1,250.00');

    return (
        <View style={styles.container}>
            <StatusBar style="light" />
            <ServiceHeader
                title="Electricity Services"
                icon="flash"
                color="#FFB300" // Amber/Gold
                navigation={navigation}
            />

            <ScrollView contentContainerStyle={styles.scrollContent}>
                {/* Quick Stats Card */}
                <View style={styles.card}>
                    <Text style={styles.cardTitle}>Current Connection</Text>
                    <Text style={styles.connectionId}>Consumer No: 123456789</Text>
                    <View style={styles.balanceRow}>
                        <View>
                            <Text style={styles.balanceLabel}>Outstanding Amount</Text>
                            <Text style={[styles.balanceValue, { color: '#D32F2F' }]}>{balance}</Text>
                        </View>
                        <TouchableOpacity style={[styles.payButton, { backgroundColor: '#FFB300' }]}>
                            <Text style={styles.payButtonText}>Pay Now</Text>
                        </TouchableOpacity>
                    </View>
                    <Text style={styles.dueDate}>Due Date: 25 Feb 2026</Text>
                </View>

                {/* Actions Grid */}
                <Text style={styles.sectionTitle}>Quick Actions</Text>
                <View style={styles.actionsContainer}>
                    <ActionButton
                        title="View Bill History"
                        icon="document-text-outline"
                        color="#FFB300"
                        onPress={() => { }}
                    />
                    <ActionButton
                        title="Report Outage"
                        icon="warning-outline"
                        color="#D32F2F"
                        onPress={() => { }}
                    />
                    <ActionButton
                        title="New Connection"
                        icon="add-circle-outline"
                        color="#2E7D32"
                        onPress={() => { }}
                    />
                    <ActionButton
                        title="Usage Analytics"
                        icon="bar-chart-outline"
                        color="#1976D2"
                        onPress={() => { }}
                    />
                </View>
            </ScrollView>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#F8F9FA',
    },
    header: {
        height: 180,
        width: '100%',
        justifyContent: 'flex-end',
        shadowColor: "#000",
        shadowOffset: { width: 0, height: 4 },
        shadowOpacity: 0.1,
        shadowRadius: 10,
        elevation: 5,
        borderBottomLeftRadius: 30,
        borderBottomRightRadius: 30,
        overflow: 'hidden',
    },
    headerBackground: {
        ...StyleSheet.absoluteFillObject,
    },
    headerContent: {
        padding: 20,
        paddingBottom: 30,
    },
    backButton: {
        marginBottom: 15,
        width: 40,
        height: 40,
        borderRadius: 20,
        backgroundColor: 'rgba(255,255,255,0.2)',
        alignItems: 'center',
        justifyContent: 'center',
    },
    titleContainer: {
        flexDirection: 'row',
        alignItems: 'center',
    },
    iconContainer: {
        padding: 10,
        borderRadius: 12,
        marginRight: 15,
    },
    headerTitle: {
        fontSize: 24,
        fontWeight: 'bold',
        color: '#FFF',
    },
    scrollContent: {
        padding: 20,
        paddingTop: 25,
    },
    card: {
        backgroundColor: '#FFF',
        borderRadius: 20,
        padding: 20,
        marginBottom: 25,
        shadowColor: "#000",
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.05,
        shadowRadius: 10,
        elevation: 3,
    },
    cardTitle: {
        fontSize: 16,
        color: '#666',
        fontWeight: '600',
        marginBottom: 5,
    },
    connectionId: {
        fontSize: 14,
        color: '#999',
        marginBottom: 20,
    },
    balanceRow: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: 15,
    },
    balanceLabel: {
        fontSize: 12,
        color: '#666',
        marginBottom: 4,
    },
    balanceValue: {
        fontSize: 24,
        fontWeight: 'bold',
    },
    payButton: {
        paddingVertical: 10,
        paddingHorizontal: 25,
        borderRadius: 10,
        shadowColor: "#FFB300",
        shadowOpacity: 0.3,
        shadowRadius: 5,
        elevation: 5,
    },
    payButtonText: {
        color: '#FFF',
        fontWeight: 'bold',
        fontSize: 14,
    },
    dueDate: {
        fontSize: 12,
        color: '#FF5722',
        fontWeight: '500',
    },
    sectionTitle: {
        fontSize: 18,
        fontWeight: 'bold',
        color: '#333',
        marginBottom: 15,
    },
    actionsContainer: {
        backgroundColor: '#FFF',
        borderRadius: 20,
        padding: 5,
        shadowColor: "#000",
        shadowOffset: { width: 0, height: 2 },
        shadowOpacity: 0.05,
        shadowRadius: 10,
        elevation: 3,
    },
    actionButton: {
        flexDirection: 'row',
        alignItems: 'center',
        padding: 15,
        borderBottomWidth: 1,
        borderBottomColor: '#F0F0F0',
    },
    actionIcon: {
        width: 40,
        height: 40,
        borderRadius: 10,
        alignItems: 'center',
        justifyContent: 'center',
        marginRight: 15,
    },
    actionText: {
        fontSize: 16,
        color: '#333',
        flex: 1,
        fontWeight: '500',
    },
});
