import React from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
} from 'react-native';
import { colors, spacing, typography, borderRadius, shadows } from '../../theme/colors';

const ServiceProvidersScreen = ({ navigation, route }) => {
  const { serviceType } = route.params;

  const providers = {
    electricity: [
      { id: 'dgvcl', name: 'DGVCL', fullName: 'Dakshin Gujarat Vij Company Limited', coverage: 'South Gujarat' },
      { id: 'torrent', name: 'Torrent Power', fullName: 'Torrent Power Limited', coverage: 'Ahmedabad, Gandhinagar' },
      { id: 'ugvcl', name: 'UGVCL', fullName: 'Uttar Gujarat Vij Company Limited', coverage: 'North Gujarat' },
    ],
    gas: [
      { id: 'ggl', name: 'Gujarat Gas', fullName: 'Gujarat Gas Limited', coverage: 'All Gujarat' },
      { id: 'adani', name: 'Adani Gas', fullName: 'Adani Total Gas Limited', coverage: 'Major Cities' },
    ],
    water: [
      { id: 'amc', name: 'AMC Water', fullName: 'Ahmedabad Municipal Corporation', coverage: 'Ahmedabad' },
      { id: 'smc', name: 'SMC Water', fullName: 'Surat Municipal Corporation', coverage: 'Surat' },
    ],
    property: [
      { id: 'amc-prop', name: 'AMC Property', fullName: 'Ahmedabad Municipal Corporation', coverage: 'Ahmedabad' },
      { id: 'smc-prop', name: 'SMC Property', fullName: 'Surat Municipal Corporation', coverage: 'Surat' },
    ],
  };

  const currentProviders = providers[serviceType] || [];

  const handleProviderSelect = (provider) => {
    navigation.navigate('DocumentUpload', {
      serviceType,
      providerId: provider.id,
      providerName: provider.name,
    });
  };

  const getServiceTitle = () => {
    const titles = {
      electricity: 'Electricity Providers',
      gas: 'Gas Providers',
      water: 'Water Providers',
      property: 'Property Services',
    };
    return titles[serviceType] || 'Service Providers';
  };

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity
          onPress={() => navigation.goBack()}
          style={styles.backButton}
        >
          <Text style={styles.backIcon}>←</Text>
        </TouchableOpacity>
        <View style={styles.headerContent}>
          <Text style={styles.headerTitle}>{getServiceTitle()}</Text>
          <Text style={styles.headerSubtitle}>Select your service provider</Text>
        </View>
      </View>

      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Info Banner */}
        <View style={styles.infoBanner}>
          <Text style={styles.infoText}>
            Select your service provider to continue with the application
          </Text>
        </View>

        {/* Providers List */}
        <View style={styles.providersContainer}>
          {currentProviders.map((provider) => (
            <TouchableOpacity
              key={provider.id}
              onPress={() => handleProviderSelect(provider)}
              activeOpacity={0.7}
              style={styles.providerCard}
            >
              <View style={styles.providerInitial}>
                <Text style={styles.providerInitialText}>
                  {provider.name.charAt(0)}
                </Text>
              </View>
              <View style={styles.providerContent}>
                <Text style={styles.providerName}>{provider.name}</Text>
                <Text style={styles.providerFullName}>{provider.fullName}</Text>
                <Text style={styles.coverageText}>{provider.coverage}</Text>
              </View>
              <View style={styles.providerArrow}>
                <Text style={styles.arrowIcon}>→</Text>
              </View>
            </TouchableOpacity>
          ))}
        </View>

        {/* Help Section */}
        <View style={styles.helpSection}>
          <Text style={styles.helpTitle}>Can't find your provider?</Text>
          <Text style={styles.helpText}>
            Contact our support team for assistance
          </Text>
          <TouchableOpacity
            onPress={() => navigation.navigate('Support')}
            style={styles.helpButton}
          >
            <Text style={styles.helpButtonText}>Contact Support</Text>
          </TouchableOpacity>
        </View>

        <View style={{ height: spacing.xxl }} />
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: colors.neutral.bg,
  },
  header: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingHorizontal: spacing.md,
    paddingVertical: spacing.md,
    backgroundColor: colors.neutral.surface,
    borderBottomWidth: 1,
    borderBottomColor: colors.neutral.border,
  },
  backButton: {
    marginRight: spacing.sm,
  },
  backIcon: {
    fontSize: typography.h2,
    color: colors.text.primary,
  },
  headerContent: {
    flex: 1,
  },
  headerTitle: {
    fontSize: typography.h3,
    fontWeight: typography.bold,
    color: colors.text.primary,
  },
  headerSubtitle: {
    fontSize: typography.tiny,
    color: colors.text.secondary,
    marginTop: spacing.xs,
  },
  infoBanner: {
    backgroundColor: colors.primary.bg,
    marginHorizontal: spacing.md,
    marginTop: spacing.lg,
    padding: spacing.md,
    borderRadius: borderRadius.md,
    borderLeftWidth: 4,
    borderLeftColor: colors.primary.main,
  },
  infoText: {
    fontSize: typography.small,
    color: colors.primary.main,
    lineHeight: 20,
  },
  providersContainer: {
    paddingHorizontal: spacing.md,
    marginTop: spacing.lg,
  },
  providerCard: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: colors.neutral.surface,
    borderRadius: borderRadius.md,
    padding: spacing.md,
    marginBottom: spacing.md,
    borderWidth: 1,
    borderColor: colors.neutral.border,
    ...shadows.sm,
  },
  providerInitial: {
    width: 56,
    height: 56,
    borderRadius: borderRadius.sm,
    backgroundColor: colors.primary.main,
    alignItems: 'center',
    justifyContent: 'center',
    marginRight: spacing.md,
  },
  providerInitialText: {
    fontSize: typography.h2,
    fontWeight: typography.bold,
    color: colors.text.inverse,
  },
  providerContent: {
    flex: 1,
  },
  providerName: {
    fontSize: typography.h4,
    fontWeight: typography.bold,
    color: colors.text.primary,
    marginBottom: spacing.xs,
  },
  providerFullName: {
    fontSize: typography.small,
    color: colors.text.secondary,
    marginBottom: spacing.xs,
  },
  coverageText: {
    fontSize: typography.tiny,
    color: colors.text.secondary,
  },
  providerArrow: {
    width: 32,
    height: 32,
    borderRadius: borderRadius.sm,
    backgroundColor: colors.neutral.bg,
    alignItems: 'center',
    justifyContent: 'center',
  },
  arrowIcon: {
    fontSize: typography.h3,
    color: colors.primary.main,
    fontWeight: typography.bold,
  },
  helpSection: {
    marginHorizontal: spacing.md,
    marginTop: spacing.lg,
    padding: spacing.lg,
    backgroundColor: colors.neutral.surface,
    borderRadius: borderRadius.md,
    alignItems: 'center',
    borderWidth: 1,
    borderColor: colors.neutral.border,
    ...shadows.sm,
  },
  helpTitle: {
    fontSize: typography.h4,
    fontWeight: typography.bold,
    color: colors.text.primary,
    marginBottom: spacing.sm,
  },
  helpText: {
    fontSize: typography.small,
    color: colors.text.secondary,
    textAlign: 'center',
    marginBottom: spacing.md,
  },
  helpButton: {
    backgroundColor: colors.primary.main,
    paddingHorizontal: spacing.lg,
    paddingVertical: spacing.md,
    borderRadius: borderRadius.sm,
  },
  helpButtonText: {
    fontSize: typography.body,
    fontWeight: typography.semibold,
    color: colors.text.inverse,
  },
});

export default ServiceProvidersScreen;
