import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity, ActivityIndicator, Dimensions } from 'react-native';
import { getUserDetails, logoutUser } from '../services/api';
import { SafeAreaView } from 'react-native-safe-area-context';
import { Ionicons } from '@expo/vector-icons';
import { LinearGradient } from 'expo-linear-gradient';

const { width } = Dimensions.get('window');

const StatCard = ({ label, value, icon, color, bg }) => (
    <View style={styles.statCard}>
        <View style={styles.statHeader}>
            <View style={[styles.statIconContainer, { backgroundColor: bg }]}>
                <Ionicons name={icon} size={20} color={color} />
            </View>
            <Text style={[styles.statValue, { color: color }]}>{value}</Text>
        </View>
        <Text style={styles.statLabel}>{label}</Text>
    </View>
);

const ServiceCard = ({ title, subtitle, description, icon, gradientColors, iconColor, bgLight, onPress }) => (
    <TouchableOpacity style={styles.serviceCard} onPress={onPress}>
        <View style={styles.serviceHeader}>
            <View style={[styles.serviceIconContainer, { backgroundColor: bgLight }]}>
                <Ionicons name={icon} size={28} color={iconColor} />
            </View>
        </View>

        <Text style={styles.serviceTitle}>{title}</Text>
        <Text style={styles.serviceSubtitle}>{subtitle}</Text>
        <Text style={styles.serviceDescription}>{description}</Text>

        <View style={styles.serviceFooter}>
            <Text style={[styles.serviceAction, { color: iconColor }]}>Start Application</Text>
            <Ionicons name="arrow-forward" size={16} color={iconColor} />
        </View>
    </TouchableOpacity>
);

export default function DashboardScreen({ navigation }) {
    const [user, setUser] = useState(null);
    const [stats, setStats] = useState({ applications: 0, pending: 0, completed: 0 });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const userData = await getUserDetails();
            setUser(userData);
            // Mock stats for now as per web dashboard logic
            setStats({
                applications: 12, // Mock data matching web vibe
                pending: 4,
                completed: 8
            });
        } catch (error) {
            console.error('Failed to fetch data', error);
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = async () => {
        await logoutUser();
        navigation.replace('Login');
    };

    if (loading) {
        return (
            <View style={styles.loadingContainer}>
                <ActivityIndicator size="large" color="#4F46E5" />
            </View>
        );
    }

    return (
        <SafeAreaView style={styles.container} edges={['top']}>
            <ScrollView contentContainerStyle={styles.scrollContent} showsVerticalScrollIndicator={false}>

                {/* Header */}
                <View style={styles.header}>
                    <View>
                        <View style={styles.verifiedBadge}>
                            <Ionicons name="shield-checkmark" size={12} color="#166534" />
                            <Text style={styles.verifiedText}>VERIFIED CITIZEN</Text>
                        </View>
                        <Text style={styles.greeting}>Welcome Back,</Text>
                        <Text style={styles.username}>{user?.full_name || 'Resident'}</Text>
                    </View>
                    <TouchableOpacity onPress={handleLogout} style={styles.profileButton}>
                        <View style={styles.profileIcon}>
                            <Ionicons name="person" size={20} color="#4F46E5" />
                        </View>
                    </TouchableOpacity>
                </View>

                {/* Banner */}
                <View style={styles.bannerContainer}>
                    <LinearGradient
                        colors={['#EFF6FF', '#EEF2FF']} // blue-50 to indigo-50
                        start={{ x: 0, y: 0 }}
                        end={{ x: 1, y: 1 }}
                        style={styles.banner}
                    >
                        <View style={styles.bannerContent}>
                            <Text style={styles.bannerTitle}>Unified Services Portal</Text>
                            <Text style={styles.bannerText}>
                                Your single dashboard for all government services. Track applications and explore new schemes.
                            </Text>
                            <View style={styles.bannerActions}>
                                <TouchableOpacity style={styles.primaryButton}>
                                    <Text style={styles.primaryButtonText}>New Application</Text>
                                </TouchableOpacity>
                            </View>
                        </View>
                    </LinearGradient>
                </View>

                {/* Stats Grid */}
                <View style={styles.statsGrid}>
                    <StatCard
                        label="Total Applications"
                        value={stats.applications}
                        icon="documents"
                        color="#2563EB"
                        bg="#EFF6FF"
                    />
                    <StatCard
                        label="In Progress"
                        value={stats.pending}
                        icon="time"
                        color="#D97706"
                        bg="#FFFBEB"
                    />
                    <StatCard
                        label="Completed"
                        value={stats.completed}
                        icon="checkmark-circle"
                        color="#16A34A"
                        bg="#F0FDF4"
                    />
                    <StatCard
                        label="Notifications"
                        value="2"
                        icon="notifications"
                        color="#9333EA"
                        bg="#FAF5FF"
                    />
                </View>

                {/* Main Services */}
                <Text style={styles.sectionTitle}>Explore Services</Text>
                <View style={styles.servicesGrid}>
                    <ServiceCard
                        title="Utility Services"
                        subtitle="उपयोगिता सेवाएं"
                        description="Electricity, Gas, Water & Property"
                        icon="flash"
                        iconColor="#D97706"
                        bgLight="#FFFBEB"
                        onPress={() => navigation.navigate('Electricity')}
                    />
                    <ServiceCard
                        title="Business Registration"
                        subtitle="व्यवसाय पंजीकरण"
                        description="GST, TAN, PAN & Formation"
                        icon="briefcase"
                        iconColor="#2563EB"
                        bgLight="#EFF6FF"
                        onPress={() => console.log('Navigate Business')}
                    />
                    <ServiceCard
                        title="Government Grants"
                        subtitle="सरकारी अनुदान"
                        description="Startup, MSME & Incentives"
                        icon="gift"
                        iconColor="#059669"
                        bgLight="#ECFDF5"
                        onPress={() => console.log('Navigate Grants')}
                    />
                </View>

                {/* Feature Highlight */}
                <View style={styles.featureCard}>
                    <View style={styles.featureIcon}>
                        <Ionicons name="sparkles" size={24} color="#4F46E5" />
                    </View>
                    <View style={styles.featureContent}>
                        <Text style={styles.featureTitle}>AI-Powered Processing</Text>
                        <Text style={styles.featureText}>
                            Our new AI system automatically extracts data from your documents.
                        </Text>
                    </View>
                </View>

            </ScrollView>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#FFFFFF',
    },
    loadingContainer: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#FFFFFF',
    },
    scrollContent: {
        padding: 20,
        paddingBottom: 40,
    },
    header: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'flex-start',
        marginBottom: 25,
    },
    verifiedBadge: {
        flexDirection: 'row',
        alignItems: 'center',
        backgroundColor: '#F0FDF4',
        paddingHorizontal: 8,
        paddingVertical: 4,
        borderRadius: 12,
        marginBottom: 8,
        alignSelf: 'flex-start',
        borderWidth: 1,
        borderColor: '#DCFCE7'
    },
    verifiedText: {
        fontSize: 10,
        fontWeight: 'bold',
        color: '#166534',
        marginLeft: 4,
    },
    greeting: {
        fontSize: 14,
        color: '#64748B',
        fontWeight: '600',
        textTransform: 'uppercase',
        letterSpacing: 0.5,
    },
    username: {
        fontSize: 28,
        fontWeight: 'bold',
        color: '#0F172A',
        marginTop: 4,
    },
    profileButton: {
        padding: 4,
    },
    profileIcon: {
        width: 48,
        height: 48,
        borderRadius: 16,
        backgroundColor: '#EEF2FF',
        alignItems: 'center',
        justifyContent: 'center',
        borderWidth: 1,
        borderColor: '#E0E7FF'
    },
    bannerContainer: {
        borderRadius: 24,
        overflow: 'hidden',
        marginBottom: 30,
        borderWidth: 1,
        borderColor: '#E2E8F0',
    },
    banner: {
        padding: 24,
    },
    bannerTitle: {
        fontSize: 20,
        fontWeight: 'bold',
        color: '#1E293B',
        marginBottom: 8,
    },
    bannerText: {
        fontSize: 14,
        color: '#64748B',
        lineHeight: 20,
        marginBottom: 20,
        maxWidth: '90%',
    },
    primaryButton: {
        backgroundColor: '#4F46E5',
        paddingVertical: 12,
        paddingHorizontal: 20,
