import React, { useState } from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
  Alert,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { useTheme } from '../../context/ThemeContext';

const DocumentUploadScreen = ({ navigation, route }) => {
  const theme = useTheme();
  const { service, provider } = route.params || {};
  const [uploadedDocs, setUploadedDocs] = useState([]);

  const requiredDocuments = [
    {
      id: 'identity',
      title: 'Identity Proof',
      subtitle: 'Aadhaar Card / PAN Card / Passport',
      icon: 'üÜî',
      required: true,
    },
    {
      id: 'address',
      title: 'Address Proof',
      subtitle: 'Utility Bill / Rent Agreement',
      icon: 'üè†',
      required: true,
    },
    {
      id: 'photo',
      title: 'Passport Photo',
      subtitle: 'Recent photograph',
      icon: 'üì∏',
      required: true,
    },
    {
      id: 'property',
      title: 'Property Documents',
      subtitle: 'If applicable',
      icon: 'üìÑ',
      required: false,
    },
  ];

  const handleUpload = (docId) => {
    // Simulate document upload
    Alert.alert(
      'Upload Document',
      'In production, this would open camera or file picker',
      [
        {
          text: 'Camera',
          onPress: () => {
            setUploadedDocs([...uploadedDocs, docId]);
            Alert.alert('Success', 'Document uploaded successfully!');
          },
        },
        {
          text: 'Gallery',
          onPress: () => {
            setUploadedDocs([...uploadedDocs, docId]);
            Alert.alert('Success', 'Document uploaded successfully!');
          },
        },
        { text: 'Cancel', style: 'cancel' },
      ]
    );
  };

  const handleContinue = () => {
    const requiredDocs = requiredDocuments.filter(doc => doc.required);
    const uploadedRequired = requiredDocs.filter(doc => 
      uploadedDocs.includes(doc.id)
    );

    if (uploadedRequired.length < requiredDocs.length) {
      Alert.alert('Error', 'Please upload all required documents');
      return;
    }

    // Navigate to final form
    navigation.navigate('FinalForm', { service, provider });
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Header */}
        <LinearGradient
          colors={[theme.colors.primary[600], theme.colors.primary[700]]}
          style={styles.header}
        >
          <TouchableOpacity
            onPress={() => navigation.goBack()}
            style={styles.backButton}
          >
            <Text style={styles.backIcon}>‚Üê</Text>
          </TouchableOpacity>
          <View style={styles.headerContent}>
            <Text style={styles.headerTitle}>Upload Documents</Text>
            <Text style={styles.headerSubtitle}>
              {service?.title || 'Service Application'}
            </Text>
            <Text style={styles.headerDescription}>
              Provider: {provider?.name || 'Selected Provider'}
            </Text>
          </View>
        </LinearGradient>

        {/* Info Banner */}
        <View style={styles.section}>
          <View style={styles.infoBanner}>
            <Text style={styles.infoIcon}>üí°</Text>
            <View style={styles.infoContent}>
              <Text style={styles.infoTitle}>Document Guidelines</Text>
              <Text style={styles.infoText}>
                ‚Ä¢ Upload clear, readable images{'\n'}
                ‚Ä¢ File size should be less than 5MB{'\n'}
                ‚Ä¢ Accepted formats: JPG, PNG, PDF
              </Text>
            </View>
          </View>
        </View>

        {/* Progress */}
        <View style={styles.section}>
          <View style={styles.progressContainer}>
            <View style={styles.progressStep}>
              <View style={[styles.progressDot, styles.progressDotActive]}>
                <Text style={styles.progressDotText}>‚úì</Text>
              </View>
              <Text style={styles.progressLabel}>Service</Text>
            </View>
            <View style={styles.progressLine} />
            <View style={styles.progressStep}>
              <View style={[styles.progressDot, styles.progressDotActive]}>
                <Text style={styles.progressDotText}>‚úì</Text>
              </View>
              <Text style={styles.progressLabel}>Provider</Text>
            </View>
            <View style={styles.progressLine} />
            <View style={styles.progressStep}>
              <View style={[styles.progressDot, styles.progressDotCurrent]}>
                <Text style={styles.progressDotText}>3</Text>
              </View>
              <Text style={styles.progressLabel}>Documents</Text>
            </View>
            <View style={styles.progressLine} />
            <View style={styles.progressStep}>
              <View style={styles.progressDot}>
                <Text style={styles.progressDotText}>4</Text>
              </View>
              <Text style={styles.progressLabel}>Submit</Text>
            </View>
          </View>
        </View>

        {/* Documents List */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Required Documents</Text>
          {requiredDocuments.map((doc) => {
            const isUploaded = uploadedDocs.includes(doc.id);
            return (
              <View key={doc.id} style={styles.docCard}>
                <View style={styles.docHeader}>
                  <View style={styles.docIconContainer}>
                    <Text style={styles.docIcon}>{doc.icon}</Text>
                  </View>
                  <View style={styles.docInfo}>
                    <View style={styles.docTitleRow}>
                      <Text style={styles.docTitle}>{doc.title}</Text>
                      {doc.required && (
                        <View style={styles.requiredBadge}>
                          <Text style={styles.requiredText}>Required</Text>
                        </View>
                      )}
                    </View>
                    <Text style={styles.docSubtitle}>{doc.subtitle}</Text>
                  </View>
                </View>
                
                {isUploaded ? (
                  <View style={styles.uploadedContainer}>
                    <View style={styles.uploadedBadge}>
                      <Text style={styles.uploadedIcon}>‚úì</Text>
                      <Text style={styles.uploadedText}>Uploaded</Text>
                    </View>
                    <TouchableOpacity
                      onPress={() => handleUpload(doc.id)}
                      style={styles.reuploadButton}
                    >
                      <Text style={styles.reuploadText}>Re-upload</Text>
                    </TouchableOpacity>
                  </View>
                ) : (
                  <TouchableOpacity
                    onPress={() => handleUpload(doc.id)}
                    style={styles.uploadButton}
                  >
                    <Text style={styles.uploadButtonText}>Upload ‚Üí</Text>
                  </TouchableOpacity>
                )}
              </View>
            );
          })}
        </View>

        {/* Continue Button */}
        <View style={styles.section}>
          <TouchableOpacity
            onPress={handleContinue}
            activeOpacity={0.8}
          >
            <LinearGradient
              colors={[theme.colors.primary[600], theme.colors.primary[700]]}
              style={styles.continueButton}
            >
              <Text style={styles.continueButtonText}>Continue to Form ‚Üí</Text>
            </LinearGradient>
          </TouchableOpacity>
        </View>

        <View style={{ height: 40 }} />
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F8FAFC',
  },
  header: {
    paddingTop: 20,
    paddingBottom: 30,
    paddingHorizontal: 20,
    borderBottomLeftRadius: 30,
    borderBottomRightRadius: 30,
  },
  backButton: {
    width: 40,
    height: 40,
    borderRadius: 12,
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: 16,
  },
  backIcon: {
    fontSize: 24,
    color: '#FFFFFF',
    fontWeight: '700',
  },
  headerContent: {
    alignItems: 'center',
  },
  headerTitle: {
    fontSize: 28,
    fontWeight: '800',
    color: '#FFFFFF',
    marginBottom: 4,
  },
  headerSubtitle: {
    fontSize: 16,
    color: 'rgba(255, 255, 255, 0.9)',
    marginBottom: 4,
  },
  headerDescription: {
    fontSize: 14,
    color: 'rgba(255, 255, 255, 0.8)',
  },
  section: {
    paddingHorizontal: 20,
    marginTop: 24,
  },
  infoBanner: {
    flexDirection: 'row',
    backgroundColor: '#DBEAFE',
    borderRadius: 16,
    padding: 16,
  },
  infoIcon: {
    fontSize: 24,
    marginRight: 12,
  },
  infoContent: {
    flex: 1,
  },
  infoTitle: {
    fontSize: 15,
    fontWeight: '700',
    color: '#1E40AF',
    marginBottom: 4,
  },
  infoText: {
    fontSize: 13,
    color: '#1E40AF',
    lineHeight: 20,
  },
  progressContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    backgroundColor: '#FFFFFF',
    borderRadius: 16,
    padding: 20,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.05,
    shadowRadius: 4,
    elevation: 2,
  },
  progressStep: {
    alignItems: 'center',
  },
  progressDot: {
    width: 40,
    height: 40,
    borderRadius: 20,
    backgroundColor: '#E2E8F0',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: 8,
  },
  progressDotActive: {
    backgroundColor: '#10B981',
  },
  progressDotCurrent: {
    backgroundColor: '#2563EB',
  },
  progressDotText: {
    fontSize: 14,
    fontWeight: '700',
    color: '#FFFFFF',
  },
  progressLabel: {
    fontSize: 11,
    fontWeight: '600',
    color: '#64748B',
  },
  progressLine: {
    flex: 1,
    height: 2,
    backgroundColor: '#E2E8F0',
    marginHorizontal: 8,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: '700',
