import React, { useState } from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
  Alert,
  Image,
  Platform,
} from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import * as DocumentPicker from 'expo-document-picker';
import { colors, spacing, typography, borderRadius, shadows } from '../../theme/colors';

const DocumentUploadScreen = ({ navigation, route }) => {
  const { service, provider } = route.params || {};
  const [uploadedDocs, setUploadedDocs] = useState({});

  const requiredDocuments = [
    {
      id: 'identity',
      title: 'Identity Proof',
      subtitle: 'Aadhaar Card / PAN Card / Passport',
      required: true,
      formats: 'PDF, JPG, PNG',
      maxSize: '5 MB',
    },
    {
      id: 'address',
      title: 'Address Proof',
      subtitle: 'Utility Bill / Rent Agreement / Ration Card',
      required: true,
      formats: 'PDF, JPG, PNG',
      maxSize: '5 MB',
    },
    {
      id: 'photo',
      title: 'Passport Photo',
      subtitle: 'Recent photograph (white background)',
      required: true,
      formats: 'JPG, PNG',
      maxSize: '2 MB',
    },
    {
      id: 'property',
      title: 'Property Documents',
      subtitle: 'Property papers (if applicable)',
      required: false,
      formats: 'PDF, JPG, PNG',
      maxSize: '10 MB',
    },
  ];

  const requestPermissions = async () => {
    if (Platform.OS !== 'web') {
      const { status: cameraStatus } = await ImagePicker.requestCameraPermissionsAsync();
      const { status: mediaStatus } = await ImagePicker.requestMediaLibraryPermissionsAsync();
      
      if (cameraStatus !== 'granted' || mediaStatus !== 'granted') {
        Alert.alert(
          'Permissions Required',
          'Camera and media library permissions are required to upload documents.',
          [{ text: 'OK' }]
        );
        return false;
      }
    }
    return true;
  };

  const handleCamera = async (docId) => {
    const hasPermission = await requestPermissions();
    if (!hasPermission) return;

    try {
      const result = await ImagePicker.launchCameraAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        allowsEditing: true,
        aspect: [4, 3],
        quality: 0.8,
      });

      if (!result.canceled && result.assets && result.assets.length > 0) {
        const asset = result.assets[0];
        setUploadedDocs({
          ...uploadedDocs,
          [docId]: {
            uri: asset.uri,
            type: 'image',
            name: `${docId}_${Date.now()}.jpg`,
            size: asset.fileSize || 0,
          },
        });
        Alert.alert('Success', 'Document captured successfully!');
      }
    } catch (error) {
      console.error('Camera error:', error);
      Alert.alert('Error', 'Failed to capture image. Please try again.');
    }
  };

  const handleGallery = async (docId) => {
    const hasPermission = await requestPermissions();
    if (!hasPermission) return;

    try {
      const result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        allowsEditing: true,
        aspect: [4, 3],
        quality: 0.8,
      });

      if (!result.canceled && result.assets && result.assets.length > 0) {
        const asset = result.assets[0];
        setUploadedDocs({
          ...uploadedDocs,
          [docId]: {
            uri: asset.uri,
            type: 'image',
            name: `${docId}_${Date.now()}.jpg`,
            size: asset.fileSize || 0,
          },
        });
        Alert.alert('Success', 'Document uploaded successfully!');
      }
    } catch (error) {
      console.error('Gallery error:', error);
      Alert.alert('Error', 'Failed to select image. Please try again.');
    }
  };

  const handleDocument = async (docId) => {
    try {
      const result = await DocumentPicker.getDocumentAsync({
        type: ['application/pdf', 'image/*'],
        copyToCacheDirectory: true,
      });

      if (result.type === 'success') {
        setUploadedDocs({
          ...uploadedDocs,
          [docId]: {
            uri: result.uri,
            type: result.mimeType?.includes('pdf') ? 'pdf' : 'image',
            name: result.name,
            size: result.size || 0,
          },
        });
        Alert.alert('Success', 'Document uploaded successfully!');
      }
    } catch (error) {
      console.error('Document picker error:', error);
      Alert.alert('Error', 'Failed to select document. Please try again.');
    }
  };

  const handleUpload = (docId) => {
    Alert.alert(
      'Upload Document',
      'Choose upload method',
      [
        {
          text: 'Take Photo',
          onPress: () => handleCamera(docId),
        },
        {
          text: 'Choose from Gallery',
          onPress: () => handleGallery(docId),
        },
        {
          text: 'Select Document',
          onPress: () => handleDocument(docId),
        },
        { text: 'Cancel', style: 'cancel' },
      ]
    );
  };

  const handleRemove = (docId) => {
    Alert.alert(
      'Remove Document',
      'Are you sure you want to remove this document?',
      [
        {
          text: 'Remove',
          style: 'destructive',
          onPress: () => {
            const newDocs = { ...uploadedDocs };
            delete newDocs[docId];
            setUploadedDocs(newDocs);
          },
        },
        { text: 'Cancel', style: 'cancel' },
      ]
    );
  };

  const handleContinue = () => {
    const requiredDocs = requiredDocuments.filter(doc => doc.required);
    const uploadedRequired = requiredDocs.filter(doc => uploadedDocs[doc.id]);

    if (uploadedRequired.length < requiredDocs.length) {
      Alert.alert('Missing Documents', 'Please upload all required documents to continue.');
      return;
    }

    navigation.navigate('FinalForm', { service, provider, documents: uploadedDocs });
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return Math.round(bytes / Math.pow(k, i) * 100) / 100 + ' ' + sizes[i];
  };

  const getUploadProgress = () => {
    const required = requiredDocuments.filter(doc => doc.required).length;
    const uploaded = requiredDocuments.filter(doc => doc.required && uploadedDocs[doc.id]).length;
    return { uploaded, required };
  };

  const progress = getUploadProgress();

  return (
    <SafeAreaView style={styles.container}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity
          onPress={() => navigation.goBack()}
          style={styles.backButton}
        >
          <Text style={styles.backIcon}>←</Text>
        </TouchableOpacity>
        <View style={styles.headerContent}>
          <Text style={styles.headerTitle}>Upload Documents</Text>
          <Text style={styles.headerSubtitle}>
            {provider?.name || 'Service Provider'} • {service?.title || 'Service'}
          </Text>
        </View>
      </View>

      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Progress Card */}
        <View style={styles.progressCard}>
          <View style={styles.progressHeader}>
            <Text style={styles.progressTitle}>Upload Progress</Text>
            <Text style={styles.progressCount}>
              {progress.uploaded} of {progress.required} required
            </Text>
          </View>
          <View style={styles.progressBarContainer}>
            <View 
              style={[
                styles.progressBar, 
                { width: `${(progress.uploaded / progress.required) * 100}%` }
              ]} 
            />
          </View>
          <Text style={styles.progressText}>
            {progress.uploaded === progress.required 
              ? 'All required documents uploaded!' 
              : `${progress.required - progress.uploaded} more required`}
          </Text>
        </View>

        {/* Info Banner */}
        <View style={styles.infoBanner}>
          <View style={styles.infoBadge}>
            <Text style={styles.infoBadgeText}>TIPS</Text>
          </View>
          <View style={styles.infoContent}>
            <Text style={styles.infoText}>
              • Ensure documents are clear and readable{'\n'}
              • File size should be less than specified limit{'\n'}
              • Accepted formats: PDF, JPG, PNG
            </Text>
          </View>
        </View>

        {/* Documents List */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Required Documents</Text>
          {requiredDocuments.map((doc) => {
            const isUploaded = uploadedDocs[doc.id];
            return (
              <View key={doc.id} style={styles.docCard}>
                {/* Document Header */}
                <View style={styles.docHeader}>
                  <View style={[
                    styles.docInitial,
                    isUploaded && styles.docInitialUploaded
                  ]}>
                    <Text style={styles.docInitialText}>
                      {isUploaded ? '✓' : doc.title.charAt(0)}
                    </Text>
                  </View>
                  <View style={styles.docInfo}>
                    <View style={styles.docTitleRow}>
                      <Text style={styles.docTitle}>{doc.title}</Text>
                      {doc.required && !isUploaded && (
                        <View style={styles.requiredBadge}>
                          <Text style={styles.requiredText}>Required</Text>
                        </View>
                      )}
                      {isUploaded && (
                        <View style={styles.uploadedBadge}>
                          <Text style={styles.uploadedText}>Uploaded</Text>
                        </View>
                      )}
                    </View>
                    <Text style={styles.docSubtitle}>{doc.subtitle}</Text>
                    <View style={styles.docMeta}>
                      <Text style={styles.docMetaText}>
                        {doc.formats} • Max {doc.maxSize}
                      </Text>
                    </View>
                  </View>
                </View>

                {/* Uploaded Document Preview */}
                {isUploaded && (
                  <View style={styles.uploadedContainer}>
                    {isUploaded.type === 'image' && (
                      <Image 
                        source={{ uri: isUploaded.uri }} 
                        style={styles.documentPreview}
                        resizeMode="cover"
                      />
                    )}
                    <View style={styles.uploadedInfo}>
                      <Text style={styles.uploadedFileName} numberOfLines={1}>
                        {isUploaded.name}
                      </Text>
                      <Text style={styles.uploadedFileSize}>
                        {formatFileSize(isUploaded.size)}
                      </Text>
                    </View>
                    <View style={styles.uploadedActions}>
                      <TouchableOpacity
                        onPress={() => handleUpload(doc.id)}
                        style={styles.actionButton}
                      >
                        <Text style={styles.actionButtonText}>Replace</Text>
                      </TouchableOpacity>
                      <TouchableOpacity
                        onPress={() => handleRemove(doc.id)}
                        style={[styles.actionButton, styles.actionButtonDanger]}
                      >
                        <Text style={[styles.actionButtonText, styles.actionButtonTextDanger]}>
                          Remove
                        </Text>
                      </TouchableOpacity>
                    </View>
                  </View>
                )}

                {/* Upload Button */}
                {!isUploaded && (
                  <TouchableOpacity
                    onPress={() => handleUpload(doc.id)}
                    style={styles.uploadButton}
                  >
                    <Text style={styles.uploadButtonText}>Upload Document</Text>
                    <Text style={styles.uploadButtonIcon}>+</Text>
                  </TouchableOpacity>
                )}
              </View>
            );
          })}
        </View>

        {/* Continue Button */}
        <View style={styles.section}>
          <TouchableOpacity
            onPress={handleContinue}
            activeOpacity={0.7}
            style={[
              styles.continueButton,
              progress.uploaded < progress.required && styles.continueButtonDisabled
            ]}
            disabled={progress.uploaded < progress.required}
          >
            <Text style={styles.continueButtonText}>
              {progress.uploaded < progress.required 
                ? 'Upload Required Documents' 
                : 'Continue to Application Form'}
            </Text>
            {progress.uploaded === progress.required && (
              <Text style={styles.continueButtonArrow}>→</Text>
            )}
          </TouchableOpacity>
        </View>

        <View style={{ height: spacing.xxl }} />
      </ScrollView>
    </SafeAreaView>
  );
};

