import React, { createContext, useState, useContext, useEffect } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import api from '../services/api';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [token, setToken] = useState(null);
  const [demoMode, setDemoMode] = useState(false);

  useEffect(() => {
    loadStoredAuth();
  }, []);

  const loadStoredAuth = async () => {
    try {
      const storedToken = await AsyncStorage.getItem('token');
      const storedUser = await AsyncStorage.getItem('user');
      const storedDemoMode = await AsyncStorage.getItem('demoMode');
      
      if (storedToken && storedUser) {
        setToken(storedToken);
        setUser(JSON.parse(storedUser));
        setDemoMode(storedDemoMode === 'true');
        if (storedDemoMode !== 'true') {
          api.defaults.headers.common['Authorization'] = `Bearer ${storedToken}`;
        }
      }
    } catch (error) {
      console.error('Failed to load auth:', error);
    } finally {
      setLoading(false);
    }
  };

  const login = async (email, password) => {
    try {
      const formData = new FormData();
      formData.append('username', email);
      formData.append('password', password);

      const response = await api.post('/auth/login', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const { access_token, user: userData } = response.data;
      
      await AsyncStorage.setItem('token', access_token);
      await AsyncStorage.setItem('user', JSON.stringify(userData));
      await AsyncStorage.setItem('demoMode', 'false');
      
      setToken(access_token);
      setUser(userData);
      setDemoMode(false);
      api.defaults.headers.common['Authorization'] = `Bearer ${access_token}`;
      
      return { success: true };
    } catch (error) {
      console.error('Login error:', error.message);
      
      // If backend is not available, use demo mode
      if (error.code === 'ERR_NETWORK' || error.code === 'ECONNABORTED') {
        console.log('Backend not available, using demo mode');
        
        // Create demo user
        const demoUser = {
          id: 1,
          email: email,
          full_name: 'Demo User',
          mobile: '9876543210',
          city: 'Ahmedabad',
        };
        
        const demoToken = 'demo_token_' + Date.now();
        
        await AsyncStorage.setItem('token', demoToken);
        await AsyncStorage.setItem('user', JSON.stringify(demoUser));
        await AsyncStorage.setItem('demoMode', 'true');
        
        setToken(demoToken);
        setUser(demoUser);
        setDemoMode(true);
        
        return { 
          success: true, 
          message: 'Demo mode: Backend not available. Using offline mode.' 
        };
      }
      
      return {
        success: false,
        message: error.response?.data?.detail || error.message || 'Login failed. Please try again.',
      };
    }
  };

  const register = async (userData) => {
    try {
      const response = await api.post('/auth/register', userData);
      return { success: true, data: response.data };
    } catch (error) {
      console.error('Registration error:', error.message);
      
      // If backend is not available, use demo mode
      if (error.code === 'ERR_NETWORK' || error.code === 'ECONNABORTED') {
        console.log('Backend not available, creating demo account');
        
        // Create demo user from registration data
        const demoUser = {
          id: 1,
          email: userData.email,
          full_name: userData.full_name,
          mobile: userData.mobile,
          city: userData.city,
        };
        
        const demoToken = 'demo_token_' + Date.now();
        
        await AsyncStorage.setItem('token', demoToken);
        await AsyncStorage.setItem('user', JSON.stringify(demoUser));
        await AsyncStorage.setItem('demoMode', 'true');
        
        return { 
          success: true, 
          data: demoUser,
          message: 'Demo mode: Account created offline.' 
        };
      }
      
      return {
        success: false,
        message: error.response?.data?.detail || error.message || 'Registration failed. Please try again.',
      };
    }
  };

  const logout = async () => {
    try {
      await AsyncStorage.removeItem('token');
      await AsyncStorage.removeItem('user');
      await AsyncStorage.removeItem('demoMode');
      setToken(null);
      setUser(null);
      setDemoMode(false);
      delete api.defaults.headers.common['Authorization'];
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  const updateUser = async (updatedData) => {
    try {
      const updatedUser = { ...user, ...updatedData };
      await AsyncStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);
    } catch (error) {
      console.error('Update user error:', error);
    }
  };

  const value = {
    user,
    token,
    loading,
    demoMode,
    login,
    register,
    logout,
    updateUser,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
