import React, { useState } from 'react';
import {
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  StyleSheet,
  SafeAreaView,
  TextInput,
} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { useTheme } from '../../context/ThemeContext';

const GovernmentGrantsScreen = ({ navigation }) => {
  const theme = useTheme();
  const [searchQuery, setSearchQuery] = useState('');

  const grants = [
    {
      id: 'startup',
      title: 'Startup Gujarat Grant',
      subtitle: '‡§∏‡•ç‡§ü‡§æ‡§∞‡•ç‡§ü‡§Ö‡§™ ‡§ó‡•Å‡§ú‡§∞‡§æ‡§§ ‡§Ö‡§®‡•Å‡§¶‡§æ‡§®',
      description: 'Financial assistance for new startups',
      icon: 'üöÄ',
      gradient: ['#3B82F6', '#2563EB'],
      amount: 'Up to ‚Çπ10 Lakhs',
      eligibility: 'New startups < 2 years',
    },
    {
      id: 'msme',
      title: 'MSME Development Grant',
      subtitle: '‡§è‡§Æ‡§è‡§∏‡§è‡§Æ‡§à ‡§µ‡§ø‡§ï‡§æ‡§∏ ‡§Ö‡§®‡•Å‡§¶‡§æ‡§®',
      description: 'Support for small & medium enterprises',
      icon: 'üè≠',
      gradient: ['#10B981', '#059669'],
      amount: 'Up to ‚Çπ25 Lakhs',
      eligibility: 'Registered MSMEs',
    },
    {
      id: 'export',
      title: 'Export Promotion Grant',
      subtitle: '‡§®‡§ø‡§∞‡•ç‡§Ø‡§æ‡§§ ‡§™‡•ç‡§∞‡•ã‡§§‡•ç‡§∏‡§æ‡§π‡§® ‡§Ö‡§®‡•Å‡§¶‡§æ‡§®',
      description: 'Incentives for export businesses',
      icon: 'üåç',
      gradient: ['#F59E0B', '#D97706'],
      amount: 'Up to ‚Çπ50 Lakhs',
      eligibility: 'Export businesses',
    },
    {
      id: 'women',
      title: 'Women Entrepreneur Grant',
      subtitle: '‡§Æ‡§π‡§ø‡§≤‡§æ ‡§â‡§¶‡•ç‡§Ø‡§Æ‡•Ä ‡§Ö‡§®‡•Å‡§¶‡§æ‡§®',
      description: 'Special grants for women-led businesses',
      icon: 'üë©‚Äçüíº',
      gradient: ['#EC4899', '#DB2777'],
      amount: 'Up to ‚Çπ15 Lakhs',
      eligibility: 'Women entrepreneurs',
    },
    {
      id: 'tech',
      title: 'Technology Innovation Grant',
      subtitle: '‡§™‡•ç‡§∞‡•å‡§¶‡•ç‡§Ø‡•ã‡§ó‡§ø‡§ï‡•Ä ‡§®‡§µ‡§æ‡§ö‡§æ‡§∞ ‡§Ö‡§®‡•Å‡§¶‡§æ‡§®',
      description: 'Funding for tech innovation',
      icon: 'üíª',
      gradient: ['#8B5CF6', '#7C3AED'],
      amount: 'Up to ‚Çπ30 Lakhs',
      eligibility: 'Tech startups',
    },
  ];

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView showsVerticalScrollIndicator={false}>
        {/* Header */}
        <LinearGradient
          colors={[theme.colors.emerald[600], theme.colors.emerald[700]]}
          style={styles.header}
        >
          <TouchableOpacity
            onPress={() => navigation.goBack()}
            style={styles.backButton}
          >
            <Text style={styles.backIcon}>‚Üê</Text>
          </TouchableOpacity>
          <View style={styles.headerContent}>
            <Text style={styles.headerTitle}>Government Grants</Text>
            <Text style={styles.headerSubtitle}>‡§∏‡§∞‡§ï‡§æ‡§∞‡•Ä ‡§Ö‡§®‡•Å‡§¶‡§æ‡§®</Text>
            <Text style={styles.headerDescription}>
              Explore funding opportunities for your business
            </Text>
          </View>
        </LinearGradient>

        {/* Search Bar */}
        <View style={styles.section}>
          <View style={styles.searchContainer}>
            <Text style={styles.searchIcon}>üîç</Text>
            <TextInput
              style={styles.searchInput}
              placeholder="Search grants..."
              value={searchQuery}
              onChangeText={setSearchQuery}
            />
          </View>
        </View>

        {/* AI Grant Finder Banner */}
        <View style={styles.section}>
          <LinearGradient
            colors={['#8B5CF6', '#7C3AED']}
            start={{ x: 0, y: 0 }}
            end={{ x: 1, y: 1 }}
            style={styles.aiBanner}
          >
            <View style={styles.aiContent}>
              <Text style={styles.aiIcon}>ü§ñ</Text>
              <View style={styles.aiText}>
                <Text style={styles.aiTitle}>AI Grant Finder</Text>
                <Text style={styles.aiDescription}>
                  Let AI find the best grants for your business
                </Text>
              </View>
            </View>
            <TouchableOpacity style={styles.aiButton}>
              <Text style={styles.aiButtonText}>Try Now ‚Üí</Text>
            </TouchableOpacity>
          </LinearGradient>
        </View>

        {/* Stats */}
        <View style={styles.section}>
          <View style={styles.statsContainer}>
            {[
              { label: 'Available', value: '50+', icon: 'üìä' },
              { label: 'Applied', value: '12', icon: 'üìù' },
              { label: 'Approved', value: '5', icon: '‚úÖ' },
            ].map((stat, index) => (
              <View key={index} style={styles.statCard}>
                <Text style={styles.statIcon}>{stat.icon}</Text>
                <Text style={styles.statValue}>{stat.value}</Text>
                <Text style={styles.statLabel}>{stat.label}</Text>
              </View>
            ))}
          </View>
        </View>

        {/* Grants List */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Featured Grants</Text>
          {grants.map((grant) => (
            <TouchableOpacity
              key={grant.id}
              onPress={() => {
                console.log('Selected grant:', grant.title);
              }}
              activeOpacity={0.8}
            >
              <LinearGradient
                colors={grant.gradient}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 1 }}
                style={styles.grantCard}
              >
                <View style={styles.grantHeader}>
                  <View style={styles.grantIconContainer}>
                    <Text style={styles.grantIcon}>{grant.icon}</Text>
                  </View>
                  <View style={styles.grantInfo}>
                    <Text style={styles.grantTitle}>{grant.title}</Text>
                    <Text style={styles.grantSubtitle}>{grant.subtitle}</Text>
                    <Text style={styles.grantDescription}>
                      {grant.description}
                    </Text>
                  </View>
                </View>
                <View style={styles.grantDetails}>
                  <View style={styles.grantDetail}>
                    <Text style={styles.detailLabel}>Amount</Text>
                    <Text style={styles.detailValue}>{grant.amount}</Text>
                  </View>
                  <View style={styles.grantDetail}>
                    <Text style={styles.detailLabel}>Eligibility</Text>
                    <Text style={styles.detailValue}>{grant.eligibility}</Text>
                  </View>
                </View>
                <TouchableOpacity style={styles.applyButton}>
                  <Text style={styles.applyButtonText}>Apply Now ‚Üí</Text>
                </TouchableOpacity>
              </LinearGradient>
            </TouchableOpacity>
          ))}
        </View>

        {/* Help Section */}
        <View style={styles.section}>
          <View style={styles.helpCard}>
            <Text style={styles.helpIcon}>üí°</Text>
            <View style={styles.helpContent}>
              <Text style={styles.helpTitle}>Need Help?</Text>
              <Text style={styles.helpText}>
                Our experts can help you find and apply for the right grants
              </Text>
            </View>
            <TouchableOpacity
              onPress={() => navigation.navigate('Support')}
              style={styles.helpButton}
            >
              <Text style={styles.helpButtonText}>Contact Us</Text>
            </TouchableOpacity>
          </View>
        </View>

        <View style={{ height: 40 }} />
      </ScrollView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F8FAFC',
  },
  header: {
    paddingTop: 20,
